INSERT INTO `aufgaben` (`auf_id`, `mdb_id`, `atyp_id`, `auf_head`, `auf_body`, `auf_stufe`, `auf_loesung`) VALUES
(1, 1, 3, 'Artikelanzahl bestimmen', 'Ermitteln Sie per Aggregatfunktion, wie viele Artikel die Artikeltabelle enthält.\0', 1, 'select count(a_id)as Anzahl from artikel'),
(2, 1, 3, 'Preisliste bestimmen', 'Aus der Preisliste2006 sollen der niedrigste, der höchste und der durchschnittliche Einzelpreis  bestimmt werden.\0', 2, 'select min(pl_einzelpreis) as Niedrigste, max(pl_einzelpreis) as Hoechste, avg(pl_einzelpreis) as Schnitt\r\nfrom preisliste2006'),
(3, 1, 3, 'Bestellpositionen zählen', 'Aus den Tabellen Bestellpositionen und Bestellungen soll herausgefunden werden, wie viele Bestellpositionen jede Bestellung enthält.\0', 3, 'select bestellungen.b_id,b_datum,count(bp_id) as Anzahl\r\nfrom bestellpos inner join bestellungen on bestellungen.b_id=bestellpos.b_id\r\ngroup by bestellungen.b_id\r\norder by Anzahl desc'),
(4, 1, 1, 'Alle Artikel mit einem "E"', 'Ermitteln Sie alle Artikel (id,bezeichner, einheit), in deren Bezeichner ein "E" vorkommt. \r\nSortieren Sie das Ergebnis nach dem Bezeichner aufsteigend.', 1, 'select a_id, a_bezeichner, a_einheit \r\nfrom artikel\r\nwhere a_bezeichner like ''%e%''\r\norder by a_bezeichner'),
(5, 1, 2, 'Artikel mit Einzelpreisen', 'Bestimmen Sie alle Artikel(bezeichner) mit ihren Preisen (einzelpreis), deren Einzelpreis >10? ist.\r\nDie Sortierung soll nach Einzelpreis absteigend erfolgen.', 2, 'select artikel.a_bezeichner,preisliste2006.pl_einzelpreis \r\nfrom artikel Inner Join preisliste2006 on preisliste2006.a_id=artikel.a_id \r\nwhere preisliste2006.pl_einzelpreis >10 \r\norder by preisliste2006.pl_einzelpreis desc '),
(6, 1, 2, 'Kunden bestellen aus Segmenten', 'Aus welchen Segmenten (s_bezeichner)hat jeweils ein Kunde(k_name) wann (b_datum) bestellt?\r\nSortierung nach Kunde aufsteigend und Datum absteigend. ', 3, 'select k_name,b_datum,s_bezeichner from\r\n((((kunden inner join bestellungen on kunden.k_id=bestellungen.k_id)\r\n           inner join bestellpos on bestellpos.b_id=bestellungen.b_id)\r\n           inner join artikel on artikel.a_id=bestellpos.a_id)\r\n           inner join segmente on segmente.s_id=artikel.s_id)\r\norder by k_name, b_datum desc '),
(7, 1, 3, 'Anzahl Bestellungen pro Segment und Kunde', 'Wieviel mal hat jeweils ein Kunde (k_name)\r\naus welchen Segmenten (s_bezeichner)  bestellt?\r\nGeben Sie das Ergebnis als Segmentanzahl sortiert nach Kundenname und Segmentbezeichner aus. ', 3, 'select k_name,s_bezeichner, count(artikel.s_id) as Segmentanzahl \r\nfrom kunden inner join bestellungen using (k_id) \r\n            inner join bestellpos using (b_id) \r\n            inner join artikel using (a_id) \r\n            inner join segmente using (s_id) \r\ngroup by k_name,artikel.s_id \r\norder by k_name, s_bezeichner'),
(45, 1, 4, 'Erste Kunden in "verkehrter" Reihenfolge', 'Erstellen Sie eine Abfrage, die\r\naus der Tabelle "Kunden" die drei ersten Kunden im Alphabet (k-name)\r\nmit allen Datenfeldern in verkehrter (absteigender) Reihenfolge ausgibt.', 1, 'select uf.* \r\nfrom (select kunden.* from kunden order by k_name limit 3) as uf \r\norder by k_name desc'),
(8, 1, 3, 'Preise im Segment', 'Ermittle den niedrigsten, höchsten und durchschnittlichen Preis je Segment.', 2, 'show index from artikel\r\n'),
(9, 1, 1, 'Anzahl Bestellpostionen je Kunde und Gesamtsu', 'Für jeden Kunden und seine Bestellungen jeweils die Anzahl der Bestellpositionen und die Rechnungssumme.', 3, 'select k_name, bestellungen.b_id,count(bp_id)as anzahl, sum(pl_einzelpreis*bp_anzahl) as summe\r\nfrom ((((kunden inner join bestellungen on kunden.k_id=bestellungen.k_id)\r\n           inner join bestellpos on bestellpos.b_id=bestellungen.b_id)\r\n           inner join artikel on artikel.a_id=bestellpos.a_id)\r\n           inner join  preisliste2006 on preisliste2006.a_id=artikel.a_id)\r\ngroup by k_name, bestellungen.b_id'),
(10, 1, 1, 'günstigster und teurster Artikel', 'Ermittle den teuersten und den günstigsten Artikel (a_bezeichner, minPreis, maxPreis)', 3, 'select a_bezeichner, pl_einzelpreis \r\nfrom artikel inner join preisliste2006 \r\n     on preisliste2006.a_id=artikel.a_id\r\nwhere pl_einzelpreis = (select min(pl_einzelpreis)from preisliste2006)or \r\n      pl_einzelpreis = (select max(pl_einzelpreis)from preisliste2006)'),
(11, 1, 2, 'Kunden mit teuren Bestellungen', 'Ermittle die drei Kunden mit den teuersten Bestellungen.', 2, 'select k_name, bestellungen.b_id,sum(pl_einzelpreis*bp_anzahl) as summe\r\nfrom ((((kunden inner join bestellungen on kunden.k_id=bestellungen.k_id)\r\n           inner join bestellpos on bestellpos.b_id=bestellungen.b_id)\r\n           inner join artikel on artikel.a_id=bestellpos.a_id)\r\n           inner join  preisliste2006 on preisliste2006.a_id=artikel.a_id)\r\ngroup by bestellungen.b_id\r\norder by summe desc\r\nlimit 3'),
(12, 1, 3, 'Anzahl Bestellungen pro Tag', 'An welchen Tagen wurde mehr als 1 Bestellung getätigt? \r\nAusgabe von Datum und Anzahl.\r\nSortierung nach Anzahl absteigend.\r\n', 1, 'select b_datum, count(b_id) as Anzahl\r\nfrom bestellungen\r\ngroup by b_datum\r\nhaving Anzahl > 1\r\norder by Anzahl desc\r\n'),
(13, 1, 1, 'ausstehende Lieferungen', 'Welche Bestellungen wurden noch nicht versandt?', 1, 'select * from bestellungen where b_versand IS NULL'),
(14, 1, 3, 'Gesamtsumme je Kunde', 'Ermitteln Sie die Gesamtsumme aller Bestellungen je Kunde.', 2, 'select k_name,                                           -- Kundenname\r\n       sum(pl_einzelpreis*bp_anzahl) as summe            -- Summe des Positionsbetrages\r\nfrom (((kunden inner join bestellungen \r\n        on kunden.k_id=bestellungen.k_id) \r\n        inner join bestellpos \r\n        on bestellpos.b_id=bestellungen.b_id) \r\n        inner join preisliste2006 \r\n        on preisliste2006.a_id=bestellpos.a_id) \r\ngroup by kunden.k_id '),
(57, 1, 1, 'Alle Kunden aus Orten mit E oder PLZ', 'Gesucht werden alle Datenfelder der Kunden, deren  mit K-Ort als\r\n2. Buchstaben ein "E" enthält oder deren K-PLZ größer als 40000 ist.\r\nDie Sortierung erfolgt nach K-Name absteigend und K-Vorname absteigend.\r\n  ', 1, 'select * \r\nfrom kunden\r\nwhere k_ort like "_E%" or k_plz > 40000\r\norder by k_name desc, k_vname desc'),
(15, 1, 3, 'Bestellungen je Artikel', 'Ermitteln Sie pro Artikel \r\na) die Anzahl der Bestellungvorgänge\r\nund \r\nb) die Gesamtmenge (Stück).', 2, 'select a_bezeichner, count(bestellpos.bp_anzahl) as Bestellungen, sum(bestellpos.bp_anzahl) as Stückzahl\r\nfrom bestellpos inner join artikel \r\n      on artikel.a_id=bestellpos.a_id \r\ngroup by artikel.a_id \r\norder by Bestellungen desc'),
(18, 1, 1, 'Stick''s finden', 'Aus der Artikeltabelle sollen alle Artikel mit Bezeichner und Einheit ausgegeben werden, \r\nderen Bezeichner die Zeichenkette ''stick'' beinhaltet. \r\nSortierung nach Bezeichner!', 1, 'select a_bezeichner, a_einheit from artikel\r\nwhere a_bezeichner like ''%stick%''\r\norder by a_bezeichner'),
(24, 1, 5, 'Auswertung von  Bestellungen', 'Ermitteln Sie den niedrigsten, den höchsten und den durchschnittlichen Wert aller Bestellung. ', 4, 'select min(Summe) as MinSum,max(Summe) as MaxSum,avg(Summe) as AvgSum from\r\n(select sum(bp_anzahl*pl_einzelpreis)as Summe from\r\n(preisliste2006 inner join bestellpos on preisliste2006.a_id=bestellpos.a_id)\r\ngroup by b_id)as test'),
(22, 1, 2, 'Artikel eines bestimmten Segments finden', 'Gesucht werden alle Artikel mit \r\nArtikel-Bezeichner, Segment-Bezeichner und Einzelpreis,\r\nderen Einzelpreis über 10,-€ liegt und die aus dem Segment ''Bücher'' kommen.\r\nSortierung nach Einzelpreis absteigend!', 2, 'select a_bezeichner, s_bezeichner, pl_einzelpreis from\r\n(segmente inner join artikel on artikel.s_id=segmente.s_id)\r\ninner join preisliste2006 on artikel.a_id = preisliste2006.a_id\r\nwhere pl_einzelpreis>10 and s_bezeichner = "bücher"\r\norder by pl_einzelpreis desc'),
(23, 1, 2, 'Bestellungen mit Artikeln für Kunden', 'Es sollen der Kundename, das Bestelldatum, die Anzahl und die Artikelbezeichnung für alle Kunden,deren Bestellungen\r\nund den entspr.Artikel gesucht werden. Kunden die noch nichts bestellt haben werden auch mit angezeigt. \r\nDoppelte Datensätze werden ausgeschlossen. Sortierung erfolgt nach Bestelldatum.', 3, 'select distinct k_name,b_datum,bp_anzahl,a_bezeichner from\r\n((kunden left join bestellungen on kunden.k_id=bestellungen.k_id)\r\nleft join bestellpos on bestellpos.b_id=bestellungen.b_id)\r\nleft join artikel on artikel.a_id=bestellpos.a_id\r\norder by b_datum'),
(25, 1, 3, 'Gesamtwert aller Bestellungen vor Datum', 'Ermitteln Sie den Gesamtwert aller Bestellungen vor dem ''2006-11-1''.', 2, 'select sum(bp_anzahl*pl_einzelpreis)as Gesamtwert from\r\n(preisliste2006 inner join bestellpos on preisliste2006.a_id=bestellpos.a_id)\r\ninner join bestellungen on bestellungen.b_id=bestellpos.b_id\r\nwhere b_datum < ''2006-11-1'''),
(26, 1, 1, 'Kundenabfrage mit Kriterien', 'Ermitteln Sie die Daten (alle Datenfelder) aller Kunden, deren Nachname ab der 2.Stelle mit ''ch'' beginnt, \r\noder die aus dem PLZ-Raum ''5'' kommen.\r\nSortierung nach Kundenname aufsteigend', 2, 'select * from kunden\r\nwhere k_name like ''_ch%'' or k_plz like ''5%''\r\norder by k_name'),
(27, 1, 6, 'Gesamtwert jeder Bestellung', 'Ermitteln Sie für jede Bestellung:\r\ndas Bestelldatum,den Kundennamen und die Anzahl der Bestellpositionen (Posten).\r\nErmitteln Sie in einem berechneten Feld ''Gesamtwert'' die Gesamt-Rechnungssumme.\r\nSortieren Sie nach ''Gesamtwert'' absteigend.\r\n', 1, 'select b_datum,k_name,count(bp_id)as Posanz,sum(pl_einzelpreis*bp_anzahl)as Gesamtwert\r\nfrom ((kunden inner join bestellungen on kunden.k_id=bestellungen.k_id)\r\n       inner join bestellpos on bestellpos.b_id=bestellungen.b_id)\r\n       inner join preisliste2006 on preisliste2006.a_id=bestellpos.a_id\r\ngroup by bestellungen.b_id\r\norder by gesamtwert desc'),
(28, 1, 1, 'alle Artikel', 'Gesucht werden alle Artikel mit allen Datenfeldern. Die Ausgabe erfolgt sortiert nach der Artikelbezeichnung.', 1, 'select * \r\nfrom artikel \r\norder by a_bezeichner'),
(29, 1, 2, 'Alle Segmente mit Artikeln', 'Ermitteln Sie aus Segmente alle s_bezeichner und \r\naus Artikel alle zugehörigen a_bezeichner.\r\nEs werden auch Segmente angezeigt, für die noch keine Artikel angelegt sind!\r\nDie Sortierung erfolgt aufsteigend nach s_bezeichner und a_bezeichner!', 1, 'select s_bezeichner, a_bezeichner \r\nfrom  artikel right join segmente\r\non segmente.s_id=artikel.s_id\r\norder by s_bezeichner, a_bezeichner\r\n'),
(30, 1, 6, 'Artikelpreise mit Rabatt', 'Bestimmen für jeden Artikel den Artikel-Bezeichner(a_bezeichner),den Einzelpreis (pl_einzelpreis), den Rabattsatz (pl_rabattsatz)\r\nund als neues, berechnetes Feld "ep_rabatt" den Einzelpreis abzgl. Rabatt. \r\nBerücksichtigen Sie die Prozentrechnung! \r\nDie Sortierung erfolgt nach ep_rabatt absteigend.', 1, 'select a_bezeichner, pl_einzelpreis, pl_rabattsatz,(pl_einzelpreis*(1-pl_rabattsatz)) as ep_rabatt\r\nfrom artikel inner join preisliste2006\r\non artikel.a_id=preisliste2006.a_id\r\norder by ep_rabatt desc'),
(31, 1, 6, 'Kunden und ihre Initialen', 'Diese Abfrage soll für jeden Kunden ... \r\na) seinen Vornamen, Nachnamen und den Wohnort in einem neuen Feld "Kunde" zusammenführen. z.B "Anke Stellmacher aus Bröllhagen"\r\nb) die Initialen des Kunden,also hier "AS", in einem neuen Feld "Initialen" dargestellen.	\r\nDie Sortierung erfolgt nach Initialen und Ort.', 1, 'select concat(k_vname," ",k_name, " aus ",k_ort)as Kunde, concat(left(k_vname,1), left(k_name,1)) as Initialen\r\nfrom kunden\r\norder by Initialen, k_ort'),
(33, 1, 6, 'Artikelpreise ohne / mit Mwst', 'Diese Abfrage soll für jeden Artikel...\r\na)den Bezeichner (a_bezeichner) und den Mehrwertsteuersatz (s_mwst)anzeigen.\r\nb)den Einzelpreis als Feld "PohneMwst" und den Einzelpreis zzgl. Mwst als Feld PmMwst darstellen.\r\nDie Sortierung erfolgt nach Mehrwertsteuersatz und Einzelpreis ohne Mwst. ', 2, 'select a_bezeichner, pl_einzelpreis as PohneMwst,s_mwst,(pl_einzelpreis*(1+s_mwst)) as PmMwst\r\nfrom (segmente inner join artikel on segmente.s_id=artikel.s_id)\r\n     inner join preisliste2006 on artikel.a_id=preisliste2006.a_id \r\norder by s_mwst, PohneMwst'),
(34, 1, 6, 'Bestellpositionen mit Preisen für Kunden', 'Gesucht werden für die Bestellungen jedes Kunden ...\r\na)Kundenname (k_name), Bestelldatum (b_datum), Artikelbezeichner (a_bezeichner), Anzahl (bp_anzahl) und Einzelpreis (pl_einzelpreis)\r\nb)Mehrwertsteuersatz (s_mwst), Gesamtpreise für jede Bestellposition mit /ohne Mwst. als neue Felder "PohneMwst" und "PmMwst".\r\nDie Sortierung erfolgt nach Bestelldatum und PohneMwst.', 3, 'select k_name,b_datum,bp_anzahl,a_bezeichner, pl_einzelpreis,\r\n       (pl_einzelpreis*bp_anzahl) as PohneMwst,s_mwst,(pl_einzelpreis*bp_anzahl*(1+s_mwst)) as PmMwst\r\nfrom ((((kunden inner join bestellungen on kunden.k_id=bestellungen.k_id)\r\n      inner join bestellpos on bestellungen.b_id=bestellpos.b_id)\r\n      inner join artikel on bestellpos.a_id=artikel.a_id)\r\n      inner join segmente on segmente.s_id=artikel.s_id)\r\n      inner join preisliste2006 on artikel.a_id=preisliste2006.a_id \r\norder by b_datum,PohneMwst'),
(35, 1, 6, 'Die drei teuersten Bestellpositionen', 'Ermittle aus allen Bestellpositionen...\r\na) die Bestellpositions-ID (bp_id)\r\nb) den Gesamtpreis für die Bestellposition als neues Feld "gesamt" (Produkt aus bp_anzahl und pl_einzelpreis)\r\nSortiere das Ergenis nach "gesamt" absteigend und begrenze es auf die ersten drei Datensätze.\r\n  ', 2, 'select bp_id,(bp_anzahl*pl_einzelpreis) as gesamt\r\nfrom bestellpos inner join preisliste2006 on bestellpos.a_id=preisliste2006.a_id\r\norder by gesamt desc\r\nlimit 0,3'),
(36, 1, 7, 'Anpassen der Mehrwertsteuer', 'In der Tabelle "segmente" soll in allen betroffenen Datensätze \r\ndie Mehrwertsteuer im Feld "s_mwst" von 16% (0.16) auf 19% (0.19)erhöht werden.', 1, 'update segmente set s_mwst=0.19 where s_mwst=0.16'),
(37, 1, 7, 'Anpassen von Preisen und Rabatten', 'In der Tabelle "preisliste2006" sollen für alle Preise, deren Einzelpreis kleiner als 10 ist und deren Rabattsatz größer als 10% ist:\r\n   a) der Einzelpreis um 20% erhöht und\r\n   b) der Rabattsatz um 20% verringert und\r\n   c) die Rabbatgrenze um 50% heraufgesetzt werden. ', 1, 'update preisliste2006\r\nset pl_einzelpreis=pl_einzelpreis*1.2,\r\n    pl_rabattsatz=pl_rabattsatz*0.8,\r\n    pl_rabattgrenze=pl_rabattgrenze*1.5\r\nwhere pl_einzelpreis<10 and pl_rabattsatz>0.1'),
(38, 1, 7, 'Hinzufügen neuer Artikel', 'In die Tabelle "Artikel" sollen 2 neue Datensätze eingefügt werden:\r\n   Nr.1 mit a_bezeichner="IR-Fernbedienung",a_einheit=10 und s_id=1\r\n   Nr.2 mit a_bezeichner="HBK-Mousepad",a_einheit=100 und s_id=3\r\n', 1, 'insert artikel(a_bezeichner,a_einheit,s_id)\r\nvalues(''R-Fernbedienung'',10,1),\r\n      (''HBK-Mousepad'',100,3)'),
(39, 1, 7, 'Hinzufügen einer neuen Bestellung', 'Die Kundin "Isabel Gallus" bestellt am 13.10.2007 folgende Artikel:\r\na) 10 DIN-A4 Ordner und b) 1 usb-Stick 2GB.\r\nAm 15.10.2007 wurde die Bestellung versandt. Fügen Sie die notwendigen Daten in die Tabellen "bestellungen" und "bestellpos" ein.', 2, NULL),
(41, 1, 7, 'Kunden löschen', 'Löschen Sie alle Kunden deren Postleitzahl im Bereich zwischen "22700" und "44700" liegt.', 1, 'delete from kunden\r\nwhere k_plz between ''22700'' and ''44700'''),
(40, 1, 7, 'Bestellungen wurden versandt', 'Alle Bestellungen, die bisher noch kein Datum für den Versand aufweisen, sollen dort das heutige eingetragen bekommen.', 2, 'update bestellungen\r\nset b_versand = curdate()\r\nwhere b_versand is null'),
(42, 1, 1, 'Die letzten 2 Artikel löschen', 'Löschen Sie die die zwei Artikel mit der größten a_id.', 1, 'delete from artikel \r\norder by a_id desc\r\nlimit 2'),
(43, 1, 1, 'Löschen des Segments 1 mit allen Artikeln', 'Löschen Sie das Segment mit der s_id=1. Gleichzeitig sollen auch alle referenzierten Artikel entfernt werden. (Löschweitergabe)', 2, 'delete artikel, segmente \r\nfrom segmente inner join artikel on segmente.s_id=artikel.s_id \r\nwhere segmente.s_id=1'),
(44, 1, 3, 'Artikel pro Segment zählen', 'Gesucht wird die Anzahl der Artikel pro Segment-ID.\r\nErgebnisse mit einer Anzahl von weniger als 6 sollen ausgeschlossen werden. \r\n', 2, 'select s_id, count(a_id)as Artikelzahl -- Auswahl der Datenfelder\r\nfrom artikel \r\ngroup by s_id                          -- Gruppierung nach s_id\r\nhaving Artikelzahl >5'),
(46, 1, 4, 'Bestellung unterschiedlicher Artikel aus Segmenten', 'Wieviele unterschiedliche Artikel haben die Kunden aus den verschiedenen Segmenten bestellt?\r\na) Ermitteln Sie mit einer Unterabfrage "UF" zunächst k_id, a_id und s_id so, dass keine doppelten Datensätze auftreten. \r\nb) Binden Sie diese "UF" in eine Select-Abfrage mit Aggregatfunktion ein. \r\nc) Sortieren Sie nach Kunden-Name und Segment-Bezeichner.', 2, 'select k_name,s_bezeichner, count(a_id) as anzahl from kunden inner join \r\n(select distinct k_id, artikel.s_id, bestellpos.a_id from bestellungen inner join bestellpos using(b_id) inner join artikel using (a_id)) as uf \r\nusing (k_id) inner join segmente using(s_id) group by kunden.k_id, segmente.s_id order by k_name, s_bezeichner\r\n\r\n'),
(47, 1, 4, 'Artikel pro Segment und gesamt zählen', 'Es soll die Artikelanzahl für jedes Segment (s_bezeichner)in einem Feld "anzahl" ermittelt werden. \r\nDarüber hinaus soll die Artikelanzahl insgesamt in einem weiteren Feld \r\n"gesamt" bestimmt werden.', 1, 'select s_bezeichner, \r\n       count(a_id) as anzahl , \r\n      (select count(a_id) from artikel) as gesamt\r\nfrom segmente inner join artikel using (s_id)\r\ngroup by segmente.s_id'),
(48, 1, 8, 'Tabellenerstellung aus Kunden und Bestellungen', 'Von allen Kunden, bei denen die Anzahl der Bestellungen kleiner 1 ist,\r\nsollen die kompletten Kundendaten in eine neue Tabelle "Mailing" \r\nStruktur wie Kunden) kopiert werden.', 1, 'create table mailing as \r\n       select kunden.* from kunden left join bestellungen using (k_id)\r\n              where isnull(b_id)\r\n-- alternativ: group by kunden.k_id\r\n--             having count(b_id)<1'),
(49, 1, 8, 'Entfernen der Tabelle "Mailing"', 'Entfernen Sie die Tabelle "Mailing" komplett aus der Datenbank!', 2, 'drop table mailing'),
(50, 1, 8, 'Anlegen einer neuen Tabelle "berater"', 'Erstellen Sie eine neue Tabelle "berater" in der die Kontaktdaten der Kundenberater enthalten sind. Also folgende Datenfelder: \r\n1) ber_id, integer,ohne Vorzeichen,nicht Null, Autowert\r\n2) ber_name, Zeichenkette(20); 3) ber_vname, Zeichenkette(20)\r\n4) ber_fon, Zeichenkette(40)', 1, 'create table berater(\r\n   ber_id int(10) unsigned not null auto_increment,\r\n   ber_name varchar(20),\r\n   ber_vname varchar(20),\r\n   ber_fon varchar(40),\r\n   PRIMARY KEY  (ber_id))'),
(51, 1, 8, 'Ändern der Tabelle "berater"', 'Fügen Sie der Tabelle "berater" ein weiteres Datenfeld "gebdat" für das Geburtsdatum des Beraters hinzu.\r\nÜberprüfen Sie Ihren Erfolg mit "show fields from ...." !', 1, 'ALTER TABLE `berater` ADD `ber_gebdat` DATE NOT NULL ;'),
(52, 1, 8, 'Entfernen von Tabellen-Spalten', 'Entfernen Sie aus der Tabelle "preisliste2006" die Felder \r\n"pl_rabattsatz" und "pl_rabattgrenze".\r\nÜberprüfen Sie das Ergebnis mit "show fields"!', 2, 'ALTER TABLE `preisliste2006`\r\n  DROP `pl_rabattsatz`,\r\n  DROP `pl_rabattgrenze`;'),
(53, 1, 8, 'Tabellenspalten entfernen und hinzufügen', 'a) Entfernen Sie aus der Tabelle "kunden" die Spalte "k_gebdat.  \r\nb) Fügen Sie dafür die neue Spalte "k_fon" vom Typ Zeichenkette(40) ein.\r\n\r\nVerwenden Sie nur eine Anweisung und überprüfen Sie Ihr Ergebnis mit "show fields" !', 3, 'ALTER TABLE `kunden`\r\n  DROP `k_gebdat`,\r\n  ADD `k_fon` varchar(40);'),
(54, 1, 8, 'Tabellenspalten ändern', 'In der Tabelle "kunden" sollen die Spalten "k_name" und "k_vname" folgendermaßen geändert werden:\r\na) "k_name wird umbenannt in "k_nachname" und die Länge der Zeichenkette auf 40 erhöht.\r\nb) "k_vname" wird umbenannt in "k_vorname".\r\nVerwenden Sie nur eine ALTER-Anweisung und übrüfen Sie Ihr Ergebnis mit "show fields"! ', 3, 'ALTER TABLE `kunden`\r\n     CHANGE `k_name` `k_nachname` varchar(40),\r\n     CHANGE `k_vname` `k_vorname` varchar(20);\r\n'),
(55, 1, 8, 'Tabelle umbenennen', 'Ändern Sie den Namen der Tabelle "preisliste2006" in "alte-preisliste2006".\r\nÜberprüfen Sie das Ergebnis mit "show tables"!', 1, 'RENAME TABLE `preisliste2006` TO `alte-preisliste2006`\r\n\r\n'),
(56, 1, 4, 'Artikelanzahl und Segmentanzahl vereinen', 'Ermitteln Sie in einer Abfrage aus der Artikeltabelle die Artikelanzahl (Feld "anzahl") \r\nund erstellen Sie ein neues Feld "bezeichnung" mit dem Inhalt "Artikel".\r\nVerfahren Sie genauso mit der Tabelle Segmente!\r\nVereinen Sie anschliessend beide Abfragen.', 1, 'select "Artikel" as bezeichner,count(a_id) as anzahl from artikel\r\nunion\r\nselect "Segmente" as bezeichner,count(s_id) as anzahl from segmente\r\n'),
(58, 1, 2, 'Artikel von Toni Seiler', 'Gesucht werden alle unterschiedlichen Artikel (a_bezeichner), die vom Kunden \r\n"Seiler, Toni" bestellt wurden. Doppelte Datensätze werden ausgeblendet.\r\nDie Sortierung erfolgt nach a_bezeichner.\r\n', 1, 'select distinct a_bezeichner\r\nfrom kunden inner join bestellungen using(k_id)\r\n     inner join bestellpos using(b_id)\r\n     inner join artikel using(a_id)\r\nwhere k_name="Seiler" and k_vname="Toni"\r\norder by a_bezeichner'),
(59, 1, 6, 'Kundenpasswörter erstellen', 'Für jeden Kunden (k_vname, k_name) soll in einem neuen Feld (k_pass)\r\nein individuelles Passwort generiert werden.\r\nDas Passwort besteht aus dem ersten Buchstaben des Vornamens gefolgt von \r\nden drei letzten Buchstaben des Nachnamens und der K_id multipliziert mit der PLZ.', 1, 'select k_plz,k_vname, k_name, concat(left(k_vname,1),right(k_name,3),k_id*k_plz) as k_pass\r\nfrom kunden'),
(60, 1, 2, 'Bestelldatum der Kunden', 'Gesucht werden die Kunden (k_name) und das Datum ihrer Bestellungen (b_datum).\r\nKunden die noch nicht bestellt haben werden mit angezeigt.\r\nDie Sortierung erfolgt nach Kundenname aufsteigend und Bestelldatum absteigend. ', 1, 'select k_name, b_datum\r\nfrom kunden left join bestellungen using(k_id)\r\norder by k_name, b_datum desc '),
(61, 1, 1, 'Anpassen der Postleitzahl', 'Die Postleitzahlen in der Kundentabelle sollen mit einer Länderkennung versehen werden. \r\nDer bisherigen Postleitzahl wird ein "D-" vorangestellt.\r\nAus "12345" wird also "D-12345"', 2, 'update kunden set k_plz=concat("D-",k_plz)'),
(62, 1, 1, 'Kunden, die in einem "Weg" wohnen', 'Listen Sie von den Kunden, in deren Datenfeld k_strasse die Zeichenkette "weg" vorkommt,\r\nalle Felder bis auf die k_id und das Geburtsdatum auf.\r\nDie Sortierung erfolgt jeweils absteigend nach PLZ und Name. ', 1, 'select k_name, k_vname,k_plz,k_ort,k_strasse \r\nfrom kunden \r\nwhere k_strasse like "%weg%" order by k_plz desc, k_name desc'),
(63, 1, 7, 'Anpassen der Rabattsätze', 'Verringern Sie in der Tabelle Preisliste2006 den Rabbatsatz aller Artikel, \r\ndie eine Rabbatgrenze unter 100 Stück haben \r\noder deren Rabattsatz 0,2 und mehr beträgt, \r\num jeweils 20%. ', 2, 'update preisliste2006\r\nset pl_rabattsatz=pl_rabattsatz*0.8\r\nwhere pl_rabattgrenze<100 or pl_rabattsatz >=0.2 '),
(64, 1, 7, 'Löschen von Bestell-Positionen', 'Löschen Sie aus der Tabelle bestellpos alle\r\nDatensätze, in denen die Artikel-Id zwischen 5 und 10 ist.', 1, 'delete from bestellpos\r\nwhere a_id between 5 and 10'),
(65, 1, 1, 'Artikel von "Amourik"', 'Gesucht werden alle unterschiedlichen Artikel (a_bezeichner), \r\ndie vom Kunden "Amourik" bestellt wurden. \r\nDoppelte Datensätze werden ausgeblendet.\r\nDie Sortierung erfolgt nach k_name und a_bezeichner.', 1, 'select distinct k_name,a_bezeichner \r\nfrom kunden inner join bestellungen using(k_id) \r\n            inner join bestellpos using(b_id) \r\n            inner join artikel using(a_id) \r\nwhere k_name="amourik"\r\norder by k_name,a_bezeichner'),
(66, 1, 7, 'Hinzufügen neuer Segmente', 'Fügen Sie in die Tabelle Segmente zwei neue Datensätze ein:\r\n1.) s_bezeichner="Blumen", s_mwst=0.19\r\n2.) s_bezeichner="Handy", s_mwst=0.19', 1, 'insert into segmente(s_bezeichner,s_mwst)\r\nvalues("Blumen",0.19),("Handy",0.19)\r\n'),
(67, 1, 1, 'Alle Artikel mit einem E an der dritten Stelle', 'Ermitteln Sie alle Artikel (a_id, a_bezeichner, a_einheit), in deren Bezeichner an der dritten Stelle ein "e"\r\nvorkommt. Sortieren Sie das Ergebnis nach dem Bezeichner aufsteigend.', 1, 'select a_id, a_bezeichner, a_einheit\r\nfrom artikel\r\nwhere a_bezeichner like "___e%"\r\norder by a_bezeichner'),
(68, 1, 7, 'Kunden ohne Bestellungen kopieren', 'Von allen Kunden, bei denen die Anzahl der Bestellungen kleiner 1 ist, sollen die kompletten Datensätze in\r\neine neue Tabelle „Mailing“ (Struktur wie Kunden) kopiert werden, um diese anzuschreiben.', 1, NULL),
(69, 1, 7, 'Löschen von Kunden aus der Mailing-Tabelle', 'Die Kunden mit Postleitzahlen „587xx“ wurden bereits angeschrieben. Löschen Sie diese aus der Tabelle\r\n„Mailing“.', 1, NULL),
(70, 1, 7, 'Löschen der Mailingtabelle', 'Nach Abschluss der Werbemassnahme soll die Tabelle „Mailing“ komplett gelöscht werden.', 1, NULL),
(71, 3, 1, 'Einen bestimmten Endkunden finden', 'Gesucht werden alle Endkunden (alle Datenfelder), die „Schiller“ heißen, \r\nmit „Frau“ angesprochen werden und die Postleitzahl 58706 oder 58709 haben.\r\nDie Sortierung erfolgt nach Vorname und Straße jeweils	absteigend.', 1, 'Select * \r\nfrom endkunden\r\nwhere   ek_name=“Schiller“ and ek_anrede=“Frau“ and\r\n	(ek_plz=58706 or ek_plz=58709)\r\norder by ek_vorname desc, ek_strasse desc');
