(765, 173, 6, 14, 'select k_name,b_datum,s_bezeichner\r\nfrom (((kunden inner join bestellungen on kunden.k_id = bestellungen.k_id) inner join bestellpos\r\non bestellungen.b_id = bestellpos.b_id) inner join artikel on bestellpos.a_id = artikel.a_id) inner join segmente on artikel.s_id = segmente.s_id\r\norder by k_name asc , b_datum desc'),
(762, 173, 28, 13, 'select *\r\nfrom artikel\r\norder by a_bezeichner asc'),
(763, 173, 26, 13, 'select *\r\nfrom kunden\r\nwhere k_name like ''_ch%'' or k_plz like ''5%''\r\norder by k_name asc'),
(764, 35, 30, 15, 'select a_bezeichner, pl_einzelpreis, pl_rabattsatz,(pl_einzelpreis*(1-pl_rabattsatz)) as ep_rabatt\r\nfrom artikel inner join preisliste2006\r\non artikel.a_id=preisliste2006.a_id\r\norder by ep_rabatt desc'),
(643, 120, 22, 10, 'select a_bezeichner,s_bezeichner,pl_einzelpreis from segmente inner join artikel on segmente.s_id = artikel.s_id inner join preisliste2006 on artikel.a_id=preisliste2006.a_id where pl_einzelpreis >10 and s_bezeichner like(''Bücher'') order by pl_einzelpreis desc'),
(644, 124, 22, 10, 'SELECT a_bezeichner, a_bezeichner, pl_einzelpreis\r\nFROM artikel INNER JOIN preisliste2006 ON artikel.a_id = preisliste2006.a_id INNER JOIN segmente ON artikel.s_id = segmente.s_id\r\nWHERE pl_einzelpreis > 10 AND s_bezeichner LIKE "Bücher"\r\nORDER BY pl_einzelpreis DESC'),
(15, 52, 6, 5, 'Select s_bezeichner, k_name , b_datum\r\nFrom ((((Kunden INNER JOIN bestellungen on bestellungen.k_id = Kunden.k_id)\r\n               INNER JOIN bestellpos on bestellpos.b_id = bestellungen.b_id)\r\n               INNER JOIN artikel on artikel.a_id = bestellpos.a_id )\r\n               INNER JOIN Segmente on segmente.s_id = artikel.s_id)\r\nOrder BY k_name, b_datum desc'),
(16, 57, 6, 5, 'select s_bezeichner, k_name, b_datum\r\nfrom  (((Kunden inner join Bestellungen on bestellungen.k_id = kunden.k_id)\r\n      inner join bestellpos on bestellpos.b_id = bestellungen.b_id)\r\n      inner join artikel on artikel.a_id = bestellpos.a_id)\r\n      inner join segmente on segmente.s_id = artikel.s_id\r\nOrder by k_name, b_datum desc   '),
(17, 53, 15, 5, 'SELECT count(*) as Anzahl FROM Artikel inner join(bestellpos inner join bestellungen on bestellpos.b_id = bestellungen.k_id ) on bestellpos.a_id = Artikel.s_id'),
(737, 169, 22, 14, 'select artikel.a_bezeichner, segmente.s_bezeichner, preisliste2006.pl_einzelpreis from (segmente inner join artikel on artikel.s_id = segmente.s_id) inner join  preisliste2006 on artikel.a_id = preisliste2006.a_id\r\nwhere preisliste2006.pl_einzelpreis > 10 and segmente.s_bezeichner = ''Bücher''\r\norder by preisliste2006.pl_einzelpreis desc'),
(19, 55, 6, 5, 'SELECT Kunden.k_name AS NAME, bestellungen.b_datum AS DATUM\r\nFROM ((((Segmente INNER JOIN Artikel ON Segmente.s_id = Artikel.s_id) \r\n     INNER JOIN bestellpos ON Artikel.a_id = bestellpos.a_id)    \r\n     INNER JOIN bestellungen ON bestellpos.b_id = bestellungen.b_id)\r\n     INNER JOIN Kunden ON bestellungen.k_id = Kunden.k_id)\r\nORDER BY Kunden.k_name ,bestellungen.b_datum DESC'),
