INSERT INTO `loesungen` (`l_id`, `u_id`, `auf_id`, `s_id`, `l_text`) VALUES
(3055, 208, 22, 14, 'select a_bezeichner, s_bezeichner, pl_einzelpreis\r\nfrom (segmente inner join artikel\r\n      on segmente.s_id = artikel.s_id)\r\n          inner join preisliste2006\r\n             on artikel.a_id = preisliste2006.a_id\r\nwhere pl_einzelpreis < 10 \r\n     and a_bezeichner = ''bücher''  '),
(3056, 211, 6, 14, 'SELECT s_bezeichner, k_name, b_datum\r\nFROM (((kunden inner join bestellungen using (k_id))\r\ninner join bestellpos using (b_id))\r\ninner join artikel using (a_id))\r\ninner join segmente using (s_id) \r\nORDER BY k_name asc, b_datum desc'),
(3057, 207, 22, 14, 'SELECT artikel.a_bezeichner, segmente.s_bezeichner, preisliste2006.pl_einzelpreis\r\nFROM (artikel INNER JOIN segmente ON artikel.s_id=segmente.s_id) INNER JOIN preisliste2006 ON artikel.a_id=preisliste2006.a_id\r\nWHERE preisliste2006.pl_einzelpreis >10 AND segmente.s_bezeichner like ''bücher''\r\nORDER BY preisliste2006.pl_einzelpreis desc'),
(3058, 209, 5, 14, 'select a_bezeichner, pl_einzelpreis\r\nfrom artikel inner join preisliste2006\r\n     on artikel.a_id=preisliste2006.a_id\r\nwhere pl_einzelpreis >10\r\norder by pl_einzelpreis desc'),
(3059, 210, 23, 14, 'select distinct k_name, b_datum, bp_anzahl, a_bezeichner\r\nfrom (((kunden left join bestellungen on kunden.k_id = bestellungen.k_id)\r\n      left join bestellpos on bestellungen.b_id = bestellpos.b_id)\r\n       left join artikel on bestellpos.a_id = artikel.a_id)\r\norder by b_datum;'),
(3060, 208, 6, 14, 'select k_name,b_datum,s_bezeichner \r\nfrom ((((kunden inner join bestellungen \r\n         on kunden.k_id=bestellungen.k_id) \r\n                inner join bestellpos \r\n                     using (b_id)) \r\n                inner join artikel \r\n                     using (a_id)) \r\n                inner join segmente \r\n                     using (s_id)) \r\norder by k_name, b_datum desc '),
(3061, 207, 6, 14, 'SELECT kunden.k_name, segmente.s_bezeichner, bestellungen.b_datum\r\nFROM ((((kunden INNER JOIN bestellungen ON kunden.k_id = bestellungen.k_id) \r\n     INNER JOIN bestellpos ON bestellungen.b_id = bestellpos.b_id) \r\n     INNER JOIN artikel ON bestellpos.a_id = artikel.a_id) \r\n     INNER JOIN segmente ON artikel.s_id = segmente.s_id)\r\nORDER BY kunden.k_name asc, bestellungen.b_datum desc'),
(3062, 211, 23, 14, 'SELECT k_name, b_datum, bp_anzahl, a_bezeichner\r\nFROM ((kunden left join bestellungen using (k_id) )\r\n   left join bestellpos using (b_id) )\r\n   left join artikel using (a_id)\r\nORDER BY b_datum'),
(3063, 208, 23, 14, 'select distinct k_name, b_datum, bp_anzahl, a_bezeichner\r\nfrom ((( kunden left join bestellungen\r\n           using (k_id))\r\n         left join bestellpos\r\n           using (b_id))\r\n         left join artikel\r\n           using (a_id))\r\norder by b_datum '),
(3064, 207, 23, 14, 'SELECT DISTINCT kunden.k_name, bestellungen.b_datum, bestellpos.bp_anzahl, artikel.a_bezeichner\r\nFROM (((kunden LEFT JOIN bestellungen USING(k_id)) \r\n     LEFT JOIN bestellpos USING(b_id))\r\n     LEFT JOIN artikel USING(a_id))\r\nORDER BY bestellungen.b_datum'),
(3065, 209, 22, 14, 'select a_bezeichner, pl_einzelpreis, s_bezeichner\r\nfrom artikel inner join preisliste2006\r\n using (a_id)\r\n inner join segmente\r\n using (s_id)\r\nwhere pl_einzelpreis > 10 and s_bezeichner="Bücher"\r\norder by pl_einzelpreis desc'),
(3066, 208, 30, 15, 'select a_bezeichner, pl_einzelpreis, pl_rabattsatz, (pl_einzelpreis * (1-pl_rabattsatz)) as ep_rabatt\r\nfrom artikel inner join preisliste2006\r\n     using (a_id)'),
(3067, 211, 30, 15, 'SELECT a_bezeichner, pl_einzelpreis, pl_rabattsatz, round((pl_einzelpreis - pl_einzelpreis * pl_rabattsatz),2) as ep_rabatt \r\nFROM artikel inner join preisliste2006 using (a_id)\r\nORDER BY ep_rabatt desc'),
(3068, 214, 30, 15, 'SELECT artikel.a_bezeichner, preisliste2006.pl_einzelpreis, preisliste2006.pl_rabattsatz, (pl_einzelpreis*(1-pl_rabattsatz)) AS ep_rabatt\r\nFROM artikel INNER JOIN preisliste2006\r\nON artikel.a_id = preisliste2006.a_id\r\nORDER BY ep_rabatt DESC'),
(3069, 210, 30, 15, 'select a_bezeichner, pl_einzelpreis, pl_rabattsatz, round(pl_einzelpreis-pl_einzelpreis*pl_rabattsatz,2) as ep_rabatt\r\nfrom artikel inner join preisliste2006 on artikel.a_id = preisliste2006.a_id\r\norder by ep_rabatt desc;'),
(3070, 209, 30, 15, 'select a_bezeichner, pl_einzelpreis, pl_rabattsatz, (1-pl_rabattsatz)*pl_einzelpreis as ep_rabatt\r\nfrom preisliste2006 inner join artikel using (a_id)\r\norder by ep_rabatt desc'),
(3071, 207, 30, 15, 'SELECT artikel.a_bezeichner, preisliste2006.pl_einzelpreis, preisliste2006.pl_rabattsatz, (preisliste2006.pl_einzelpreis-(preisliste2006.pl_rabattsatz*preisliste2006.pl_einzelpreis)) as ep_rabatt\r\nFROM artikel INNER JOIN preisliste2006 USING(a_id)\r\nORDER BY ep_rabatt desc'),
(3072, 210, 31, 15, 'select concat(k_vname," ",k_name," aus ",k_ort) as kunde, concat(substr(k_vname,1,1),substr(k_name,1,1)) as initialen\r\nfrom kunden\r\norder by initialen, k_ort;'),
(3073, 208, 31, 15, 'select concat(k_vname,'' '', k_name,'' aus '', k_ort) as Kunde\r\nfrom kunden\r\norder by k_ort'),
(3074, 207, 31, 15, 'SELECT CONCAT(k_vname," ",k_name," aus ",k_ort) as Kunde, CONCAT(left(k_vname,1),left(k_name,1)) as Initialen\r\nFROM kunden\r\nORDER BY Initialen asc, k_ort asc'),
(3075, 214, 31, 15, 'SELECT CONCAT(k_vname, " ",k_name," aus ",k_ort) AS Kunde, CONCAT(left(k_vname,1), left(k_name,1)) AS Initialen\r\nFROM kunden\r\nORDER BY Initialen, kunden.k_ort'),
(3076, 211, 31, 15, 'SELECT \r\n   concat(k_vname, " ", k_name, " aus ", k_ort) as Fullname,\r\n   concat(substr(k_vname,1,1), substr(k_name,1,1)) as Initial\r\nFROM kunden\r\nORDER BY Initial, k_ort'),
(3077, 208, 33, 15, 'select a_bezeichner, s_mwst, pl_einzelpreis as PohneMwst, (pl_einzelpreis * (1 + s_mwst)) as PmMwst\r\nfrom ((segmente inner join artikel\r\n      using (s_id))\r\n        inner join preisliste2006\r\n      using (a_id))\r\norder by s_mwst'),
(3078, 210, 33, 15, 'select a_bezeichner, s_mwst, pl_einzelpreis as PohneMwst, round(pl_einzelpreis+pl_einzelpreis*s_mwst,2) as PmMwst\r\nfrom (preisliste2006 inner join artikel on preisliste2006.a_id = artikel.a_id)\r\n      inner join segmente on artikel.s_id = segmente.s_id\r\norder by s_mwst, PohneMwst;'),
(3079, 209, 31, 15, 'select concat(k_vname," ",k_name," aus ",k_ort) as Kunde, concat(left(k_vname,1),left(k_name,1)) as Initialen\r\nfrom kunden\r\norder by Initialen, k_Ort\r\n'),
(3080, 210, 35, 15, 'select bp_id, round(bp_anzahl*pl_einzelpreis,2) as gesamt\r\nfrom (bestellpos inner join artikel on bestellpos.a_id = artikel.a_id)\r\n      inner join preisliste2006 on artikel.a_id = preisliste2006.a_id\r\norder by gesamt desc\r\nlimit 0,3;'),
(3081, 211, 33, 15, 'SELECT a_bezeichner, s_mwst, pl_einzelpreis as PohneMwst, round((pl_einzelpreis * (1 + s_mwst)),2) as PmMwst\r\nFROM (artikel inner join segmente using (s_id))\r\n   inner join preisliste2006 using (a_id)\r\n\r\nORDER BY s_mwst, PohneMwst'),
(3082, 208, 35, 15, 'select bp_id, (bp_anzahl * pl_einzelpreis) as gesamt\r\nfrom ((bestellpos inner join artikel\r\n    using (a_id))\r\n        inner join preisliste2006\r\n    using (a_id))\r\norder by gesamt\r\nlimit 0,3'),
