(3263, 202, 34, 15, 'select k_name, b_datum, a_bezeichner, bp_anzahl, pl_einzelpreis,\r\n(pl_einzelpreis*bp_anzahl) as PohneMwst,\r\ns_mwst,\r\n(pl_einzelpreis*bp_anzahl*(1+s_mwst)) as PmMwst\r\nfrom (((((kunden inner join bestellungen on kunden.k_id=bestellungen.k_id)\r\ninner join bestellpos on bestellungen.b_id=bestellpos.b_id)\r\ninner join artikel on artikel.a_id=bestellpos.a_id)\r\ninner join segmente on segmente.s_id=artikel.s_id)\r\ninner join preisliste2006 on preisliste2006.a_id=artikel.a_id)\r\norder by b_datum and PohneMwst\r\n\r\n\r\n'),
(3271, 199, 35, 15, 'select bp_id, (bp_anzahl*pl_einzelpreis) as gesamt\r\nfrom bestellpos inner join artikel on artikel.a_id=bestellpos.a_id\r\ninner join preisliste2006 on artikel.a_id = preisliste2006.a_id\r\norder by gesamt desc\r\nlimit 3'),
(3272, 203, 35, 15, 'select bp_id,(bp_anzahl*pl_einzelpreis) as gesamt from bestellpos inner join preisliste2006 on bestellpos.a_id=preisliste2006.a_id order by gesamt desc limit 0,3'),
(3264, 196, 1, 16, 'SELECT COUNT(a_id) FROM artikel'),
(3265, 189, 30, 15, 'select a_bezeichner, pl_einzelpreis, pl_rabattsatz,(pl_einzelpreis*(1-pl_rabattsatz)) as ep_rabatt from artikel inner join preisliste2006 on artikel.a_id=preisliste2006.a_id order by ep_rabatt desc'),
(11454, 581, 26, 13, 'select* \r\nfrom kunden \r\nwhere k_name like ''_ch%'' or k_plz like ''5%'' \r\norder by k_name'),
(3267, 199, 33, 15, 'select a_bezeichner, s_mwst, (pl_einzelpreis)as PohneMwst, \r\n(pl_einzelpreis+(pl_einzelpreis*s_mwst)) as PmMwst\r\nfrom artikel inner join segmente on segmente.s_id=artikel.s_id\r\ninner join preisliste2006 on artikel.a_id = preisliste2006.a_id\r\norder by s_mwst, pl_einzelpreis'),
(3268, 204, 31, 15, 'select concat(k_vname," ",k_name, " aus ",k_ort)as Kunde, concat(left(k_vname,1), left(k_name,1)) as Initialen from kunden order by Initialen, k_ort'),
(3269, 198, 33, 15, 'select artikel.a_bezeichner, segmente.s_mwst, pl_einzelpreis as PohneMwst, (pl_einzelpreis*(1+s_mwst)) as PmMwst\r\nfrom (segmente inner join artikel on segmente.s_id=artikel.s_id) inner join preisliste2006 on preisliste2006.a_id=artikel.a_id\r\norder by s_mwst, PohneMwst\r\n'),
(3270, 196, 12, 16, 'SELECT COUNT(b_id) AS AnzahlderBestellungen, \r\n       b_datum\r\nFROM bestellungen\r\nGROUP BY b_datum\r\nHAVING count(b_id) > 1\r\nORDER BY AnzahlderBestellungen DESC\r\n'),
(3273, 204, 33, 15, 'select a_bezeichner, pl_einzelpreis as PohneMwst,s_mwst,(pl_einzelpreis*(1+s_mwst)) as PmMwst\r\nfrom (segmente inner join artikel on segmente.s_id=artikel.s_id) inner join preisliste2006 on artikel.a_id=preisliste2006.a_id \r\norder by s_mwst, PohneMwst'),
(3274, 195, 30, 15, 'select a_bezeichner, pl_einzelpreis, pl_rabattsatz,(pl_einzelpreis*(1-pl_rabattsatz)) as ep_rabatt from artikel inner join preisliste2006 on artikel.a_id=preisliste2006.a_id order by ep_rabatt desc'),
(3275, 200, 30, 15, 'select a_bezeichner, pl_einzelpreis, pl_rabattsatz,(pl_einzelpreis*(1-pl_rabattsatz)) as ep_rabatt \r\nfrom artikel inner join preisliste2006 on artikel.a_id=preisliste2006.a_id \r\norder by ep_rabatt desc'),
(3276, 196, 2, 16, 'SELECT MIN(pl_einzelpreis) AS niedrigste, \r\n       MAX(pl_einzelpreis) AS hoechste, \r\n       ROUND(AVG(pl_einzelpreis),2) AS durchschnitt\r\nFROM preisliste2006'),
(3277, 195, 5, 14, 'SELECT artikel.a_bezeichner,preisliste2006.pl_einzelpreis \r\nFROM artikel Inner Join preisliste2006 on preisliste2006.a_id=artikel.a_id \r\nWHERE preisliste2006.pl_einzelpreis >10 order by preisliste2006.pl_einzelpreis desc '),
(3278, 190, 30, 15, 'select a_bezeichner, pl_einzelpreis, pl_rabattsatz,(pl_einzelpreis*(1-pl_rabattsatz)) as ep_rabatt from artikel inner join preisliste2006 on artikel.a_id=preisliste2006.a_id order by ep_rabatt desc'),
(3279, 190, 31, 15, 'select concat(k_vname," ",k_name, " aus ",k_ort)as Kunde, concat(left(k_vname,1), left(k_name,1)) as Initialen from kunden order by Initialen, k_ort'),
(3280, 199, 34, 15, 'select k_name,b_datum,bp_anzahl,a_bezeichner, pl_einzelpreis, (pl_einzelpreis*bp_anzahl) as PohneMwst,s_mwst,(pl_einzelpreis*bp_anzahl*(1+s_mwst)) as PmMwst from kunden \r\ninner join bestellungen on kunden.k_id=bestellungen.k_id\r\ninner join bestellpos on bestellungen.b_id=bestellpos.b_id\r\ninner join artikel on bestellpos.a_id=artikel.a_id\r\ninner join segmente on segmente.s_id=artikel.s_id\r\ninner join preisliste2006 on artikel.a_id=preisliste2006.a_id \r\norder by b_datum,PohneMwst'),
(11452, 581, 18, 13, 'select a_bezeichner, a_einheit \r\nfrom artikel \r\nwhere a_bezeichner like  ''%stick%'' \r\norder by a_bezeichner'),
(11453, 581, 28, 13, 'select* \r\nfrom artikel \r\norder by a_bezeichner'),
(3282, 194, 33, 15, 'SELECT a_bezeichner, s_mwst, (pl_einzelpreis) AS PohneMwst, (pl_einzelpreis * (1+s_mwst)) AS PmMwst \r\nFROM artikel INNER JOIN preisliste2006 ON artikel.a_id=preisliste2006.a_id INNER JOIN segmente ON artikel.s_id=segmente.s_id ORDER BY s_mwst, PohneMwst'),
(3283, 204, 35, 15, 'select bp_id,(bp_anzahl*pl_einzelpreis) as gesamt\r\nfrom bestellpos inner join preisliste2006\r\non bestellpos.a_id=preisliste2006.a_id\r\norder by gesamt desc limit 0,3'),
(3284, 198, 35, 15, 'select bestellpos.bp_id, (bp_anzahl * pl_einzelpreis) as gesamt\r\nfrom bestellpos bestellpos inner join preisliste2006 on preisliste2006.a_id=bestellpos.a_id\r\norder by gesamt desc limit 3\r\n'),
(3294, 197, 30, 15, 'select a_bezeichner, pl_einzelpreis, pl_rabattsatz, pl_einzelpreis*(1-pl_rabattsatz) as ep_rabatt\r\nfrom artikel inner join preisliste2006\r\non artikel.a_id=preisliste2006.a_id\r\norder by ep_rabatt DESC'),
(3285, 195, 22, 14, 'SELECT a_bezeichner, s_bezeichner, pl_einzelpreis \r\nFROM (segmente inner join artikel on artikel.s_id=segmente.s_id) inner join preisliste2006 on artikel.a_id = preisliste2006.a_id \r\nWHERE pl_einzelpreis>10 and s_bezeichner = "bücher" order by pl_einzelpreis desc'),
(3286, 203, 33, 15, 'select a_bezeichner, pl_einzelpreis as PohneMwst,s_mwst,(pl_einzelpreis*(1+s_mwst)) as PmMwst from (segmente inner join artikel on segmente.s_id=artikel.s_id) inner join preisliste2006 on artikel.a_id=preisliste2006.a_id order by s_mwst, PohneMwst'),
(3287, 190, 33, 15, '\r\nselect a_bezeichner, pl_einzelpreis as PohneMwst,s_mwst,(pl_einzelpreis*(1+s_mwst)) as PmMwst from (segmente inner join artikel on segmente.s_id=artikel.s_id) inner join preisliste2006 on artikel.a_id=preisliste2006.a_id order by s_mwst, PohneMwst');
INSERT INTO `loesungen` (`l_id`, `u_id`, `auf_id`, `s_id`, `l_text`) VALUES
(3288, 196, 8, 16, 'SELECT s_bezeichner, \r\n       MIN(pl_einzelpreis) AS niedrigste, \r\n       MAX(pl_einzelpreis) AS hoechste, \r\n       AVG(pl_einzelpreis) AS durchschnitt\r\nFROM segmente INNER JOIN artikel USING(s_id) \r\n              INNER JOIN preisliste2006 USING(a_id)\r\nGROUP BY s_bezeichner'),
(3289, 194, 35, 15, 'SELECT bp_id, (bp_anzahl * pl_einzelpreis) as gesamt\r\nFROM bestellpos INNER JOIN preisliste2006 ON bestellpos.a_id=preisliste2006.a_id\r\nORDER BY gesamt DESC \r\nLIMIT 0,3'),
(3290, 190, 35, 15, '\r\nselect bp_id,(bp_anzahl*pl_einzelpreis) as gesamt from bestellpos inner join preisliste2006 on bestellpos.a_id=preisliste2006.a_id order by gesamt desc limit 0,3'),
(3291, 190, 34, 15, 'select k_name,b_datum,bp_anzahl,a_bezeichner, pl_einzelpreis, (pl_einzelpreis*bp_anzahl) as PohneMwst,s_mwst,(pl_einzelpreis*bp_anzahl*(1+s_mwst)) as PmMwst from ((((kunden inner join bestellungen on kunden.k_id=bestellungen.k_id) inner join bestellpos on bestellungen.b_id=bestellpos.b_id) inner join artikel on bestellpos.a_id=artikel.a_id) inner join segmente on segmente.s_id=artikel.s_id) inner join preisliste2006 on artikel.a_id=preisliste2006.a_id order by b_datum,PohneMwst'),
(3292, 202, 1, 16, 'select count(a_id) as Anzahl \r\nfrom artikel'),
(3293, 202, 12, 16, 'select b_datum, count(b_id) as Anzahl from bestellungen \r\ngroup by b_datum having Anzahl >1\r\norder by Anzahl desc'),
(3295, 202, 2, 16, 'select min(pl_einzelpreis) as Niedrigste, \r\nmax(pl_einzelpreis) as Hoechste, \r\navg(pl_einzelpreis) as Schnitt \r\nfrom preisliste2006'),
(3296, 193, 29, 14, 'select segmente.s_bezeichner, artikel.a_bezeichner\r\nfrom segmente\r\nleft join artikel using (s_id)\r\norder by segmente.s_bezeichner, artikel.a_bezeichner\r\n'),
(3297, 193, 5, 14, 'select artikel.a_bezeichner, preisliste2006.pl_einzelpreis\r\nfrom artikel\r\nleft join preisliste2006 on artikel.a_id = preisliste2006.a_id\r\nwhere preisliste2006.pl_einzelpreis > 10\r\norder by preisliste2006.pl_einzelpreis desc '),
(3299, 193, 6, 14, 'select kunden.k_name, segmente.s_bezeichner, bestellungen.b_datum\r\nfrom kunden\r\njoin bestellungen using ( k_id )\r\njoin bestellpos using ( b_id ) \r\njoin artikel using ( a_id )\r\njoin segmente using ( s_id )\r\norder by k_name, b_datum desc '),
(3300, 193, 23, 14, 'select kunden.k_name, bestellungen.b_datum, bestellpos.bp_anzahl, artikel.a_bezeichner\r\nfrom kunden\r\njoin bestellungen using ( k_id )\r\njoin bestellpos using ( b_id )\r\njoin artikel using ( a_id )\r\norder by b_datum'),
(3466, 200, 2, 16, 'select min(pl_einzelpreis) as Niedrigste, max(pl_einzelpreis) as Höchste, avg(pl_einzelpreis) as Durchschnitt\r\nfrom preisliste2006'),
(3301, 193, 30, 15, 'select artikel.a_bezeichner, preisliste2006.pl_einzelpreis, preisliste2006.pl_rabattsatz,\r\npreisliste2006.pl_einzelpreis - (preisliste2006.pl_einzelpreis * preisliste2006.pl_rabattsatz) as ep_rabatt\r\nfrom artikel, preisliste2006\r\nwhere artikel.a_id = preisliste2006.a_id\r\norder by ep_rabatt desc\r\n'),
(3302, 193, 31, 15, 'select concat(kunden.k_vname," ",kunden.k_name," aus ",kunden.k_ort) as Kunde,\r\nconcat(left(kunden.k_vname,1), left(kunden.k_name,1)) as Initialien\r\nfrom kunden order by Initialien, kunden.k_ort'),
(3303, 193, 33, 15, 'select artikel.a_bezeichner,preisliste2006.pl_einzelpreis as PohneMwst, segmente.s_mwst,\r\npreisliste2006.pl_einzelpreis +(preisliste2006.pl_einzelpreis*segmente.s_mwst) as PmMwst\r\nfrom artikel, segmente, preisliste2006\r\nwhere \r\nartikel.s_id = segmente.s_id and\r\nartikel.a_id = preisliste2006.a_id\r\norder by segmente.s_mwst, PmMwst'),
(3304, 193, 35, 15, 'select bestellpos.bp_id, bestellpos.bp_anzahl * preisliste2006.pl_einzelpreis as gesamt\r\nfrom bestellpos, artikel, preisliste2006\r\nwhere \r\nartikel.a_id=bestellpos.a_id\r\nand\r\nartikel.a_id = preisliste2006.a_id\r\norder by gesamt desc limit 0,3'),
(3305, 193, 34, 15, 'select kunden.k_name, bestellungen.b_datum, artikel.a_bezeichner, bestellpos.bp_anzahl, preisliste2006.pl_einzelpreis, segmente.s_mwst, bestellpos.bp_anzahl*preisliste2006.pl_einzelpreis as PohneMwst, segmente.s_mwst, bestellpos.bp_anzahl*preisliste2006.pl_einzelpreis + (bestellpos.bp_anzahl*preisliste2006.pl_einzelpreis*segmente.s_mwst ) as PmitMwst\r\n\r\nfrom kunden, bestellungen, bestellpos, artikel, preisliste2006, segmente\r\nwhere\r\nkunden.k_id = bestellungen.k_id\r\nand\r\nbestellungen.b_id = bestellpos.b_id\r\nand\r\nbestellpos.a_id = artikel.a_id\r\nand \r\nartikel.a_id = preisliste2006.a_id\r\nand\r\nartikel.s_id = segmente.s_id\r\norder by bestellungen.b_datum, PohneMwst'),
(3306, 193, 1, 16, 'select max(artikel.a_id) from artikel'),
(3307, 193, 12, 16, 'select b_datum, count(*) as cnt from bestellungen\r\ngroup by b_datum\r\nhaving cnt > 1'),
(3308, 193, 2, 16, 'select min(pl_einzelpreis) as minPreis, max(pl_einzelpreis) as maxPreis,\r\navg(pl_einzelpreis) as durchPreis from preisliste2006\r\n'),
(3309, 193, 8, 16, 'select min(preisliste2006.pl_einzelpreis) as min, max(preisliste2006.pl_einzelpreis) as max,\r\navg(preisliste2006.pl_einzelpreis) as durch, segmente.s_bezeichner as segment\r\nfrom artikel, preisliste2006, segmente\r\nwhere artikel.a_id = preisliste2006.a_id and\r\nartikel.s_id = segmente.s_id\r\ngroup by segmente.s_bezeichner\r\n'),
(3310, 197, 31, 15, 'select concat(k_vname," ",k_name," aus ",k_ort) as Kunde, \r\nconcat(left(k_vname,1),left(k_name,1)) as Initialen\r\nfrom kunden\r\norder by Initialen,k_ort'),
(3311, 197, 33, 15, 'select a_bezeichner, pl_einzelpreis as PohneMwst, s_mwst, pl_einzelpreis*(1+s_mwst) as PmMwst\r\nfrom preisliste2006 inner join \r\n(segmente inner join artikel on segmente.s_id=artikel.s_id)\r\non artikel.a_id = preisliste2006.a_id\r\norder by s_mwst,PohneMwst'),
(3312, 197, 35, 15, 'select bp_id, bp_anzahl*pl_einzelpreis as gesamt\r\nfrom bestellpos inner join \r\n(artikel inner join preisliste2006 on artikel.a_id = preisliste2006.a_id)\r\non artikel.a_id = bestellpos.a_id\r\norder by gesamt desc limit 3'),
(3313, 197, 34, 15, 'select k_name, b_datum, a_bezeichner, bp_anzahl, pl_einzelpreis, pl_einzelpreis*bp_anzahl as PohneMwst, s_mwst,  pl_einzelpreis*(1+s_mwst)*bp_anzahl as PmMwst\r\nfrom ((((kunden inner join bestellungen on kunden.k_id = bestellungen.k_id)\r\ninner join bestellpos on bestellungen.b_id = bestellpos.b_id) \r\ninner join artikel on bestellpos.a_id = artikel.a_id)\r\ninner join segmente on segmente.s_id = artikel.s_id)\r\ninner join preisliste2006 on artikel.a_id = preisliste2006.a_id\r\norder by b_datum,PohneMwst'),
(3314, 197, 1, 16, 'select COUNT(a_id) as anzahl\r\nfrom artikel \r\n'),
(3315, 197, 12, 16, 'select b_datum, COUNT(b_datum) as anzahl\r\nfrom bestellungen\r\ngroup by b_datum having anzahl > 1\r\norder by anzahl desc\r\n'),
(3316, 197, 2, 16, 'select min(pl_einzelpreis) as Niedrigste, max(pl_einzelpreis) as Höchste, avg(pl_einzelpreis) as Durchschnitt\r\nfrom preisliste2006'),
(3317, 197, 8, 16, 'select s_bezeichner, min(pl_einzelpreis) as Niedrigste, max(pl_einzelpreis) as Höchste, avg(pl_einzelpreis) as Durchschnitt\r\nfrom segmente inner join (artikel inner join preisliste2006 on artikel.a_id = preisliste2006.a_id) \r\non segmente.s_id = artikel.s_id\r\ngroup by s_bezeichner'),
(3318, 197, 14, 16, 'select k_name, sum(bp_anzahl*pl_einzelpreis) as summe\r\nfrom kunden inner join (bestellungen inner join (bestellpos inner join \r\n(artikel inner join preisliste2006 on artikel.a_id = preisliste2006.a_id)\r\non artikel.a_id = bestellpos.a_id) on bestellungen.b_id = bestellpos.b_id) on kunden.k_id = bestellungen.k_id\r\ngroup by kunden.k_id'),
(3319, 197, 15, 16, 'select a_bezeichner, count(bestellpos.bp_anzahl) as Bestellungen, sum(bp_anzahl) as Stückzahl\r\nfrom artikel inner join bestellpos on artikel.a_id = bestellpos.a_id\r\ngroup by artikel.a_id\r\norder by Bestellungen desc'),
(3320, 197, 25, 16, 'select sum(pl_einzelpreis*bp_anzahl) as Gesamtwert \r\nfrom bestellungen inner join (bestellpos inner join \r\n(artikel inner join preisliste2006 on artikel.a_id = preisliste2006.a_id) \r\non artikel.a_id = bestellpos.a_id) on bestellungen.b_id = bestellpos.b_id\r\nwhere b_datum < ''2006-11-1'''),
(3321, 197, 44, 16, 'select segmente.s_id, s_bezeichner, count(a_id) as anzahl\r\nfrom segmente inner join artikel on segmente.s_id = artikel.s_id\r\ngroup by segmente.s_id having anzahl >= 6'),
(3322, 197, 3, 16, 'select b_id, count(b_id) as anzahl\r\nfrom bestellpos\r\ngroup by b_id'),
(3323, 197, 7, 16, 'select k_name,s_bezeichner, count(s_bezeichner) as Segmentanzahl \r\nfrom kunden inner join bestellungen using (k_id) inner join bestellpos using (b_id) inner join artikel using (a_id) inner join segmente using (s_id) \r\ngroup by k_name,s_bezeichner\r\norder by k_name, s_bezeichner'),
(3324, 196, 14, 16, 'SELECT k_name as Kunde, \r\n       SUM(pl_einzelpreis*bp_anzahl) AS Summe\r\nFROM kunden INNER JOIN bestellungen USING(k_id)\r\n            INNER JOIN bestellpos USING(b_id)\r\n            INNER JOIN artikel USING(a_id)\r\n            INNER JOIN preisliste2006 USING(a_id)\r\nGROUP BY k_name'),
(3325, 196, 15, 16, 'SELECT a_bezeichner, \r\n       SUM(bp_anzahl) AS Gesamtmenge, \r\n       COUNT(bp_anzahl) AS Bestellungen\r\nFROM artikel INNER JOIN bestellpos USING(a_id)\r\nGROUP BY a_bezeichner'),
(3326, 196, 25, 16, 'SELECT SUM(bp_anzahl*pl_einzelpreis) AS Gesamtwert\r\nFROM bestellungen INNER JOIN bestellpos USING(b_id)\r\n                  INNER JOIN preisliste2006 USING(a_id)\r\nWHERE b_datum < ''2006-11-1'''),
(3327, 196, 44, 16, 'SELECT s_id, \r\n       COUNT(a_id) AS Anzahl\r\nFROM artikel\r\nGROUP BY s_id\r\nHAVING COUNT(a_id) >= 6'),
(3328, 196, 3, 16, 'SELECT bestellungen.b_id, \r\n       b_datum, \r\n       COUNT(bp_id) AS Anzahl\r\nFROM bestellungen INNER JOIN bestellpos USING(b_id)\r\nGROUP BY bestellungen.b_id'),
(3329, 196, 7, 16, 'SELECT k_name,\r\n       s_bezeichner, \r\n       COUNT(artikel.s_id) AS Segmentanzahl\r\nFROM kunden INNER JOIN bestellungen USING(k_id)\r\n            INNER JOIN bestellpos USING(b_id)\r\n            INNER JOIN artikel USING(a_id)\r\n            INNER JOIN segmente USING(s_id)\r\nGROUP BY k_name, \r\n         artikel.s_id\r\nORDER BY k_name,\r\n         s_bezeichner'),
(3330, 200, 31, 15, 'select concat(k_vname," ",k_name," aus ",k_ort)as Kunde,\r\nconcat(left(k_vname,1),left(k_name,1))as Initialen\r\nfrom kunden\r\nOrder by Initialen,k_ort'),
(3331, 200, 33, 15, 'select a_bezeichner,pl_einzelpreis as PohneMwst,s_mwst,(pl_einzelpreis * (1+s_mwst)) as PmMwst\r\nfrom (segmente inner join artikel on segmente.s_id=artikel.s_id) inner join preisliste2006 on artikel.a_id=preisliste2006.a_id\r\norder by s_mwst,PohneMwst'),
