(10100, 532, 34, 15, 'SELECT k.k_name AS Kundenname,\r\n       b.b_datum AS Bestelldatum, \r\n       a.a_bezeichner AS Artikelbezeichnung, \r\n       bp.bp_anzahl AS Anzahl,\r\n       pl.pl_einzelpreis AS Einzelpreis,\r\n       s.s_mwst AS Mehrwertsteuersatz,\r\n       (bp.bp_anzahl*pl.pl_einzelpreis) AS PohneMwst,\r\n       ROUND((bp.bp_anzahl*(pl.pl_einzelpreis*(1+s.s_mwst))),2) AS PmitMwst\r\nFROM kunden AS k INNER JOIN bestellungen AS b USING (k_id)\r\n     INNER JOIN bestellpos AS bp USING (b_id) \r\n     INNER JOIN artikel AS a USING (a_id)\r\n     INNER JOIN preisliste2006 AS pl USING (a_id)\r\n     INNER JOIN segmente AS s USING (s_id)\r\nORDER BY Bestelldatum ASC, PohneMwst ASC;'),
(4458, 237, 22, 14, ''),
(4459, 237, 6, 14, ''),
(6135, 361, 34, 15, 'SELECT k.k_name AS ''Kundenname'', b.b_datum AS ''Bestelldatum'', a.a_bezeichner AS ''Artikelbezeichnung'', bp.bp_anzahl AS ''bestellte Anzahl'', p.pl_einzelpreis AS ''Einzelpreis'', (p.pl_einzelpreis * bp.bp_anzahl) AS PohneMwst, s.s_mwst, (p.pl_einzelpreis*bp.bp_anzahl*(1+s.s_mwst)) AS PmMwst \r\nFROM kunden AS k INNER JOIN bestellungen AS b USING(k_id) INNER JOIN bestellpos AS bp USING(b_id) INNER JOIN preisliste2006 AS p USING(a_id) INNER JOIN artikel AS a USING(a_id) INNER JOIN segmente AS s USING(s_id)\r\nORDER BY Bestelldatum, PohneMwst ASC '),
(6120, 362, 33, 15, 'SELECT a.a_bezeichner AS Artikelbezeichnung, pl.pl_einzelpreis AS ''PohneMwst'', s.s_mwst(pl.pl_einzelpreis*(1+\r\nFROM artikel AS a INNER JOIN segmente AS s USING(s_id)\r\n '),
(6121, 359, 35, 15, 'SELECT bp.bp_id, (bp.bp_anzahl*pl.pl_einzelpreis) AS gesamt\r\nFROM bestellpos AS bp INNER JOIN artikel AS a USING(a_id) INNER JOIN preisliste2006 AS pl USING(a_id) \r\nORDER BY gesamt DESC LIMIT 3'),
(6122, 360, 35, 15, 'SELECT bp.bp_id AS BestellpositionsID, (bp.bp_anzahl*pl.pl_einzelpreis) AS Gesamt\r\nFROM bestellpos AS bp INNER JOIN preisliste2006 AS pl USING(a_id)\r\nORDER BY Gesamt DESC LIMIT 0,3'),
(6123, 361, 33, 15, 'SELECT CONCAT(p.pl_einzelpreis) AS ''PohneMWST'', CONCAT(ROUND(s.s_mwst*p.pl_einzelpreis+p.pl_einzelpreis,2)) AS ''Einzelpreis zzgl. MWST'', a.a_bezeichner AS ''Artikelbezeichnung'', s.s_mwst AS ''Mehrwertsteuersatz''\r\nFROM segmente AS s INNER JOIN artikel AS a USING(s_id) INNER JOIN preisliste2006 AS p USING(a_id)\r\nORDER BY s.s_mwst, p.pl_einzelpreis ASC \r\n'),
(6124, 365, 35, 15, 'SELECT bp.bp_id AS ID, (bp.bp_anzahl*pl.pl_einzelpreis) AS Gesamtpreis\r\nFROM bestellpos AS bp LEFT JOIN artikel USING(a_id) INNER JOIN preisliste2006 AS pl USING(a_id)\r\nORDER BY Gesamtpreis desc \r\nLIMIT 0,3'),
(6125, 367, 33, 15, 'SELECT a.a_bezeichner AS Bezeichner, pl.pl_einzelpreis AS PreisohneMwst,s.s_mwst AS MwstSatz,(pl.pl_einzelpreis*(1+s.s_mwst)) AS PreismitMwst \r\nFROM segmente AS s INNER JOIN artikel AS a USING (s_id) INNER JOIN preisliste2006 AS pl USING (a_id)\r\nORDER BY s.s_mwst, PreisohneMwst'),
(7308, 416, 33, 15, 'SELECT a.a_bezeichner AS Artikelname, pl.pl_einzelpreis AS PohneMwst, (pl.pl_einzelpreis*(1+s.s_mwst)) AS PmMwst\r\nFROM segmente AS s INNER JOIN artikel AS a USING(s_id) INNER JOIN preisliste2006 AS pl USING(a_id)\r\nORDER BY s.s_mwst ASC, pl.pl_einzelpreis ASC'),
(7234, 420, 29, 14, 'SELECT s_bezeichner,a_bezeichner\r\nFROM segmente left join artikel using(s_id)\r\nORDER BY s_bezeichner, a_bezeichner'),
(7235, 420, 5, 14, 'SELECT a_bezeichner,pl_einzelpreis\r\nFROM artikel inner join preisliste2006 using(a_id)\r\nWHERE pl_einzelpreis > 10\r\nORDER BY pl_einzelpreis DESC'),
(7236, 424, 4, 13, 'Select a_id, a_bezeichner, a_einheit\r\nFrom artikel\r\nWhere a_bezeichner like ''%e%'' \r\nORDER BY a_bezeichner'),
