(6253, 358, 34, 15, 'select k_name, b_datum, bp_anzahl, a_bezeichner, pl_einzelpreis,\r\n(pl_einzelpreis*bp_anzahl) as PohneMwst, s_mwst, (pl_einzelpreis*bp_anzahl*(1+s_mwst)) as PmMwst\r\nfrom ((((kunden inner join bestellungen on kunden.k_id=bestellungen.k_id) inner join bestellpos on bestellungen.b_id=bestellpos.b_id) inner join artikel on bestellpos.a_id=artikel.a_id) inner join segmente on segmente.s_id=artikel.s_id) inner join preisliste2006 on artikel.a_id=preisliste2006.a_id\r\norder by b_datum, PohneMwst'),
(4840, 237, 25, 16, ''),
(4841, 237, 44, 16, ''),
(4843, 237, 3, 16, ''),
(6130, 363, 34, 15, 'SELECT k.k_name, b.b_datum, bp.bp_anzahl, a.a_bezeichner, p.pl_einzelpreis, (pl_einzelpreis*bp_anzahl) as PohneMwst,s_mwst,(pl_einzelpreis*bp_anzahl*(1+s_mwst)) as PmMwst\r\nFROM ((kunden as k INNER JOIN bestellungen as b USING(k_id) INNER JOIN bestellpos as bp USING(b_id) INNER JOIN artikel as a USING(a_id) INNER JOIN segmente as s USING(s_id) INNER JOIN preisliste2006 as pl USING(a_id) \r\nORDER BY b_datum,PohneMwst'),
(6293, 368, 7, 16, 'SELECT k.k_name AS Kundenname, s.s_bezeichner AS Segmentbezeichner, COUNT(b.b_id) AS Segmentanzahl\r\nFROM kunden AS k INNER JOIN bestellungen AS b USING(k_id) INNER JOIN bestellpos USING(b_id) INNER JOIN artikel USING(a_id) INNER JOIN segmente AS s USING(s_id)\r\nGROUP BY k.k_id, s.s_id\r\nORDER BY Kundenname ASC, Segmentbezeichner ASC'),
(9987, 519, 44, 16, 'SELECT s.s_id AS Segment_ID,\r\n       COUNT(a.a_id) AS Anzahl\r\nFROM artikel AS a INNER JOIN segmente AS s USING(s_id)\r\nGROUP by Segment_ID\r\nHAVING Anzahl>5;\r\n       '),
(6051, 363, 22, 14, 'SELECT a.a_bezeichner AS Artikelname, s.s_bezeichner AS Segmentname, p.pl_einzelpreis AS Einzelpreis\r\nFROM segmente AS s RIGHT JOIN artikel AS a ON s.s_id=a.s_id LEFT JOIN preisliste2006 AS p ON a.a_id=p.a_id\r\nWHERE p.pl_einzelpreis >10 AND s_bezeichner like ''Bücher''\r\nORDER BY p.pl_einzelpreis desc'),
(10011, 527, 2, 16, 'SELECT MIN(pl.pl_einzelpreis) AS Höchstpreis, MAX(pl.pl_einzelpreis) AS Niedrigstpreis, AVG(pl.pl_einzelpreis) AS Durchschnittspreis\r\nFROM preisliste2006 AS pl;'),
(6183, 368, 8, 16, 'SELECT MIN(pl.pl_einzelpreis) AS ''Niedrigster Preis'', MAX(pl.pl_einzelpreis) AS ''Hoechster Preis'', ROUND(AVG(pl.pl_einzelpreis),2) AS Durchschnittspreis\r\nFROM segmente AS s INNER JOIN artikel USING(s_id) INNER JOIN preisliste2006 AS pl USING(a_id)\r\nGROUP BY s.s_bezeichner'),
(6046, 364, 5, 14, 'SELECT a.a_bezeichner AS Artikelname, p.pl_einzelpreis AS Preis\r\nFROM artikel AS a INNER JOIN preisliste2006 AS p ON a.a_id=p.pl_id\r\nWHERE p.pl_einzelpreis>10\r\nORDER BY p.pl_einzelpreis DESC'),
(6043, 362, 5, 14, 'SELECT pl.pl_einzelpreis AS Einzelpreis, a.a_bezeichner AS Artikelname \r\nFROM preisliste2006 AS pl INNER JOIN artikel AS a ON pl.a_id=a.a_id\r\nWHERE pl.pl_einzelpreis >10\r\nORDER BY pl.pl_einzelpreis desc'),
(6044, 369, 22, 14, 'SELECT a.a_bezeichner AS Artikelname, s.s_bezeichner AS Segmente, p.pl_einzelpreis AS Einzelpreis\r\nFROM segmente AS s INNER JOIN artikel AS a ON s.s_id=a.s_id INNER JOIN preisliste2006 AS p ON a.a_id=p.a_id\r\nWHERE p.pl_einzelpreis >10 AND s_bezeichner LIKE ''Bücher''\r\nORDER BY p.pl_einzelpreis desc'),
(6045, 368, 22, 14, 'SELECT a.a_bezeichner AS Artikel, s.s_bezeichner AS Segment, pl.pl_einzelpreis AS Einzelpreis\r\nFROM segmente AS s RIGHT JOIN artikel AS a ON s.s_id=a.s_id LEFT JOIN preisliste2006 AS pl ON a.a_id=pl.a_id\r\nWHERE pl.pl_einzelpreis >10 AND s.s_bezeichner LIKE ''Bücher''\r\nORDER BY pl.pl_einzelpreis desc'),
(6056, 364, 22, 14, 'SELECT s.s_bezeichner AS Segmentname, a.a_bezeichner AS Artikelname, p.pl_einzelpreis AS Preis\r\nFROM segmente AS s INNER JOIN artikel AS a ON s.s_id=a.a_id INNER JOIN preisliste2006 AS p ON a.a_id=p.pl_id\r\nWHERE p.pl_einzelpreis>10 AND s.s_bezeichner LIKE ''Bücher''\r\nORDER BY p.pl_Einzelpreis DESC'),
(5956, 350, 22, 14, 'select artikel.a_bezeichner, segmente.s_bezeichner, preisliste2006.pl_einzelpreis\r\nfrom segmente inner join artikel on segmente.s_id = artikel.s_id inner join preisliste2006 on artikel.a_id = preisliste2006.a_id\r\nwhere preisliste2006.pl_einzelpreis > 10 and segmente.s_id = 2\r\norder by preisliste2006.pl_einzelpreis desc'),
(5890, 254, 45, 19, 'SELECT dk.*\r\nFROM (SELECT *\r\nFROM kunden\r\nORDER BY k_name ASC \r\nLIMIT 3) AS dk\r\nORDER BY k_name DESC;'),
(5894, 347, 18, 13, 'select a_id, a_bezeichner, a_einheit from artikel where a_bezeichner like ''%stick%'' order by a_bezeichner'),
(5895, 347, 28, 13, 'select * from artikel order by a_bezeichner'),
(5896, 347, 26, 13, 'select * from kunden where k_name like ''_ch%'' or k_plz like ''5%'' order by k_name'),
(5897, 349, 4, 13, 'SELECT a_id, a_bezeichner, a_einheit\r\nFROM artikel\r\nWHERE a_bezeichner LIKE "%E%"\r\nORDER BY a_bezeichner'),
(5898, 349, 18, 13, 'SELECT a_bezeichner, a_einheit\r\nFROM artikel\r\nWHERE a_bezeichner LIKE "%stick%"\r\nORDER BY a_bezeichner'),
(5899, 349, 28, 13, 'SELECT *\r\nFROM artikel\r\nORDER BY a_bezeichner'),
(6052, 359, 5, 14, 'SELECT a.a_bezeichner AS Artikelname, p.pl_einzelpreis AS Einzelpreis\r\nFROM artikel AS a LEFT JOIN preisliste2006 AS p ON a.a_id = p.a_id\r\nWHERE p.pl_einzelpreis >10\r\nORDER BY p.pl_einzelpreis desc'),
(6047, 365, 22, 14, 'SELECT s.s_bezeichner AS Segment, a.a_bezeichner AS Artikelname, p.pl_einzelpreis AS ''PReis pro St.''\r\nFROM segmente AS s INNER JOIN artikel AS a ON s.s_id=a.s_id INNER JOIN preisliste2006 AS p ON a.a_id=p.a_id\r\nWHERE pl_einzelpreis >10 and s.s_bezeichner = ''Bücher''\r\nORDER BY p.pl_einzelpreis desc'),
(6048, 369, 5, 14, 'SELECT a.a_bezeichner AS Artikelname, p.pl_einzelpreis AS Einzelpreis\r\nFROM artikel AS a INNER JOIN preisliste2006 AS p ON a.a_id=p.a_id\r\nWHERE p.pl_einzelpreis >10 \r\nORDER BY p.pl_einzelpreis desc'),
(6049, 361, 22, 14, 'SELECT a.a_bezeichner AS ''Artikel-Bezeichner'', s.s_bezeichner AS ''Segment-Bezeichner'', p.pl_einzelpreis AS ''Einzelpreis''\r\nFROM segmente AS s INNER JOIN artikel AS a ON s.s_id = a.s_id INNER JOIN preisliste2006 AS p ON a.a_id = p.a_id\r\nWHERE p.pl_einzelpreis >10 AND s.s_bezeichner LIKE ''Bücher''\r\nORDER BY p.pl_einzelpreis DESC'),
(6050, 367, 5, 14, 'SELECT a.a_bezeichner AS Artikelname, p.pl_einzelpreis AS Preis\r\nFROM artikel AS a INNER JOIN preisliste2006 AS p ON a.a_id=p.pl_id\r\nWHERE p.pl_einzelpreis>10\r\nORDER BY p.pl_einzelpreis DESC   '),
(4873, 254, 12, 16, ''),
(10078, 527, 8, 16, 'SELECT s.s_id, s.s_bezeichner AS Warengruppe, MIN(pl.pl_einzelpreis) AS Höchstpreis, MAX(pl.pl_einzelpreis) AS Niedrigstpreis, ROUND(AVG(pl.pl_einzelpreis),2) AS Durchschnittspreis\r\nFROM preisliste2006 AS pl INNER JOIN artikel USING(a_id)\r\n     INNER JOIN segmente AS s USING(s_id)\r\nGROUP BY s.s_id\r\nORDER BY s.s_id ASC;'),
(4874, 254, 2, 16, 'SELECT MIN(pl_einzelpreis) AS Niedrigstes, MAX(pl_einzelpreis) AS Hoechste, ROUND(AVG(pl_einzelpreis),2) AS Durchschnitt \r\nFROM preisliste2006 '),
(6320, 352, 22, 14, 'select a_bezeichner , s_bezeichner , pl_einzelpreis from artikel inner join preisliste2006 on artikel.a_id = preisliste2006.a_id inner join segmente on artikel.s_id = segmente.s_id where pl_einzelpreis > 10 and s_bezeichner = "bücher" order by pl_einzelpreis desc'),
(5912, 352, 26, 13, 'select * from kunden where k_name like "_ch%" or k_plz like "5%" order by k_name'),
(4875, 254, 8, 16, 'SELECT s.s_bezeichner AS Warengruppe, MIN(pr.pl_einzelpreis) AS Minimum, MAX(pr.pl_einzelpreis) AS Maximum, ROUND(AVG(pr.pl_einzelpreis),2) AS Durchschnitt\r\nFROM segmente AS s INNER JOIN artikel AS a USING(s_id) INNER JOIN preisliste2006 AS pr USING(a_id) \r\nGROUP BY s.s_bezeichner\r\nORDER BY s.s_bezeichner ASC'),
(6254, 346, 34, 15, 'SELECT k_name as Kundename,\r\n       b_datum as Bestelldatum,\r\n       a_bezeichner as Artikelbezeichner,\r\n       bp_anzahl as Anzahl,\r\n       pl_einzelpreis as Einzelpreis,\r\n       s_mwst as Mehrwertsteuersatz,\r\n       (bp_anzahl*pl_einzelpreis) as PohneMwst,\r\n       (bp_anzahl*(pl_einzelpreis*(1+s_mwst))) as PmMwst\r\nFROM ((((kunden INNER JOIN bestellungen using(k_id)) INNER JOIN bestellpos using(b_id)) INNER JOIN artikel using(a_id)) INNER JOIN segmente ON segmente.s_id=artikel.s_id) INNER JOIN preisliste2006 ON artikel.a_id=preisliste2006.a_id\r\nORDER BY Bestelldatum, PohneMwst'),
(6105, 359, 31, 15, 'SELECT CONCAT(k.k_vname,'' '',k.k_name,'' aus '',k_ort) AS ''Kunde'', CONCAT(LEFT (k.k_vname,1),LEFT(k.k_name,1)) AS ''Initialen''\r\nFROM kunden as k \r\nORDER BY Initialen ASC, k_ort DESC\r\n'),
(4876, 254, 14, 16, 'SELECT SUM(b.bp_anzahl*p.pl_einzelpreis) AS Umsatz, k.k_name, k.k_vname\r\nFROM kunden AS k INNER JOIN bestellungen USING(k_id) INNER JOIN bestellpos AS b USING(b_id) INNER JOIN artikel USING(a_id) INNER JOIN preisliste2006 AS p USING(a_id)\r\nGROUP BY k.k_id \r\nORDER BY Umsatz ASC'),
(5804, 254, 15, 16, 'SELECT a.a_bezeichner AS Artikelname, COUNT(bp.a_id) AS Anzahl, SUM(bp.bp_anzahl) AS Gesamtmenge\r\nFROM bestellpos AS bp INNER JOIN artikel AS a USING(a_id)\r\nGROUP BY a.a_id\r\nORDER BY Gesamtmenge DESC '),
(4878, 254, 25, 16, 'SELECT b.b_datum,SUM(bp.bp_anzahl*pl.pl_einzelpreis)AS Gesamtwert\r\nFROM bestellungen AS b INNER JOIN bestellpos AS bp USING(b_id) INNER JOIN preisliste2006 AS pl USING(a_id)\r\nGROUP BY b.b_datum\r\nHAVING b.b_datum<''2006-11-1'';'),
(5805, 254, 44, 16, 'SELECT s.s_id AS WarengruppenID, s.s_bezeichner AS Warengruppenname, COUNT(a.a_id) AS Anzahl\r\nFROM artikel AS a INNER JOIN segmente AS s USING(s_id)\r\nGROUP BY s_id\r\nHAVING Anzahl>5;'),
(5893, 347, 4, 13, 'select a_id, a_bezeichner, a_einheit\r\nfrom artikel\r\nwhere a_bezeichner like ''%e%''\r\norder by a_bezeichner'),
(6081, 360, 22, 14, 'SELECT artikel.a_bezeichner, segmente.s_bezeichner, preisliste2006.pl_einzelpreis\r\nFROM (artikel inner join segmente on artikel.s_id = segmente.s_id) inner join preisliste2006 on preisliste2006.a_id = artikel.a_id \r\nWHERE preisliste2006.pl_einzelpreis >10 and segmente.s_bezeichner like "Bücher"\r\nORDER BY preisliste2006.pl_einzelpreis desc'),
(5953, 349, 5, 14, 'SELECT a_bezeichner, pl_einzelpreis\r\nFROM artikel INNER JOIN preisliste2006 ON artikel.a_id = preisliste2006.a_id\r\nWHERE pl_einzelpreis > 10\r\nORDER BY pl_einzelpreis desc'),
(6037, 361, 5, 14, 'SELECT a.a_bezeichner AS ''Artikelbezeichner'', p.pl_einzelpreis AS ''Einzelpreis''\r\nFROM artikel AS a LEFT JOIN preisliste2006 AS p ON a.a_id = p.a_id\r\nWHERE p.pl_einzelpreis >10\r\nORDER BY p.pl_einzelpreis DESC'),
(6038, 365, 5, 14, 'SELECT a.a_bezeichner AS Artikelname, p.pl_einzelpreis AS ''Prei pro St.''\r\nFROM artikel AS a INNER JOIN preisliste2006 AS p ON a.a_id=p.a_id\r\nWHERE p.pl_einzelpreis >10\r\nORDER BY p.pl_einzelpreis desc'),
(6039, 363, 5, 14, 'SELECT a.a_bezeichner AS Artikelname, p.pl_einzelpreis AS Einzelpreis\r\nFROM artikel AS a INNER JOIN preisliste2006 AS p ON a.a_id=p.a_id\r\nWHERE p.pl_einzelpreis >10\r\nORDER BY p.pl_einzelpreis desc'),
(6040, 368, 5, 14, 'SELECT a.a_bezeichner AS Artikel, pl.pl_einzelpreis AS Einzelpreis\r\nFROM artikel AS a INNER JOIN preisliste2006 AS pl ON a.a_id=pl.a_id\r\nWhere pl.pl_einzelpreis >10\r\nORDER BY pl.pl_einzelpreis desc'),
(6041, 359, 29, 14, 'SELECT s.s_bezeichner AS Segmentname, a.a_bezeichner AS Artikelname\r\nFROM segmente AS s LEFT JOIN artikel AS a ON s.s_id = a.s_id\r\nORDER BY s.Segmentname, a.Artikelname asc'),
(4886, 237, 7, 16, ''),
(6115, 363, 33, 15, 'SELECT a.a_bezeichner AS Bezeichnung, s.s_mwst AS Mehrwertsteuer, p.pl_einzelpreis AS Einzelpreis, (p.pl_einzelpreis+p.pl_einzelpreis*s.s_mwst) AS PmMwst\r\nFROM segmente AS s INNER JOIN artikel AS a USING(s_id) INNER JOIN preisliste2006 AS p USING(a_id)\r\nORDER BY s.s_mwst ASC, PmMwst'),
(6035, 363, 29, 14, 'SELECT s.s_bezeichner AS Segmentname, a.a_bezeichner AS Artikelname\r\nFROM segmente AS s LEFT JOIN artikel AS a ON s.s_id=a.s_id\r\nORDER BY s.s_bezeichner, a.a_bezeichner asc'),
(6036, 368, 29, 14, 'SELECT s.s_bezeichner AS Segmente, a.a_bezeichner AS Artikel\r\nFROM segmente AS s LEFT JOIN artikel AS a on s.s_id=a.s_id \r\nORDER BY s_bezeichner, a_bezeichner asc'),
(6317, 326, 35, 15, 'SELECT bp_id,CONCAT(bp_anzahl*pl_einzelpreis, "€") as gesamt \r\nFROM bestellpos INNER JOIN preisliste2006 ON bestellpos.a_id = preisliste2006.a_id\r\nORDER BY gesamt desc LIMIT 3'),
(4889, 254, 7, 16, 'SELECT k.k_name AS Name, s.s_bezeichner AS Warengruppe, COUNT(a.s_id)\r\nFROM kunden AS k INNER JOIN bestellungen USING(k_id) INNER JOIN bestellpos USING(b_id) INNER JOIN artikel AS a USING(a_id) INNER JOIN segmente AS s USING(s_id)\r\nGROUP BY k.k_id, s.s_id\r\nORDER BY Name, Warengruppe'),
(5806, 254, 3, 16, 'SELECT b.b_id,b.b_datum,COUNT(bp.bp_anzahl)AS AnzahlBestellpositionen\r\nFROM bestellungen AS b INNER JOIN bestellpos AS bp USING(b_id)\r\nGROUP BY b.b_id\r\n'),
(6032, 369, 29, 14, 'SELECT s.s_bezeichner AS segmente, a.a_bezeichner AS Artikelname\r\nFROM segmente AS s LEFT JOIN artikel AS a ON s.s_id=a.s_id\r\nORDER BY s.s_bezeichner, a.a_bezeichner asc'),
(6033, 367, 29, 14, 'SELECT s.s_bezeichner AS Segmentname, a.a_bezeichner AS Artikelname\r\nFROM segmente AS s LEFT JOIN artikel AS a ON s.s_id=a.s_id\r\n\r\nORDER BY s.s_bezeichner ASC, a.a_bezeichner ASC\r\n'),
(6034, 364, 29, 14, 'SELECT s.s_bezeichner AS Segmentname, a.a_bezeichner AS Artikelname\r\nFROM segmente AS s LEFT JOIN artikel AS a ON s.s_id=a.s_id\r\nORDER BY s.s_bezeichner ASC, a.a_bezeichner ASC'),
(5955, 347, 22, 14, 'select artikel.a_bezeichner, segmente.s_bezeichner, preisliste2006.pl_einzelpreis\r\nfrom (artikel inner join segmente on artikel.s_id=segmente.s_id) inner join preisliste2006 on artikel.a_id=preisliste2006.a_id\r\nwhere preisliste2006.pl_einzelpreis >10 and segmente.s_bezeichner like "Bücher"\r\norder by preisliste2006.pl_einzelpreis desc'),
(5954, 346, 5, 14, 'SELECT a_bezeichner, pl_einzelpreis\r\nFROM artikel inner join preisliste2006 on artikel.a_id = preisliste2006.a_id\r\nWHERE pl_einzelpreis > ''10''\r\nORDER BY pl_einzelpreis desc\r\n'),
(4894, 269, 35, 15, 'select bp_id,(bp_anzahl*pl_einzelpreis) as gesamt\r\nfrom bestellpos inner join preisliste2006 on bestellpos.a_id=preisliste2006.a_id\r\norder by gesamt desc limit 3'),
(4895, 269, 34, 15, 'select k_name, b_datum, a_bezeichner, bp_anzahl, pl_einzelpreis,s_mwst,\r\n(pl_einzelpreis*bp_anzahl) as PohneMwst,\r\n(pl_einzelpreis*bp_anzahl*(1+s_mwst)) as PmMwst\r\nfrom kunden inner join bestellungen using(k_id)\r\ninner join bestellpos using(b_id)\r\ninner join artikel using(a_id)\r\ninner join preisliste2006 using(a_id)\r\ninner join segmente using(s_id)'),
(4896, 327, 31, 15, 'select concat_ws('' '',k_vname,k_name,''aus'',k_ort) as ''Kunden'', concat(left(k_vname,1),left(k_name,1)) as ''Initialien''\r\nfrom kunden\r\norder by Initialien, k_ort'),
(4897, 327, 33, 15, 'select a_bezeichner as ''Bezeichner'', s_mwst as ''Mehrwertsteuersatz'', pl_einzelpreis as ''PohneMwst'', round(pl_einzelpreis*(1 + s_mwst),2) as ''PmMwst''\r\nfrom segmente, preisliste2006, artikel'),
(4898, 328, 44, 16, 'SELECT s_bezeichner, count(a_id) as Anzahl\r\nFROM artikel natural join segmente \r\nGROUP BY s_id\r\nHAVING Anzahl > 5\r\n'),
(4899, 328, 3, 16, 'SELECT bestellungen.b_id, count(bp_id) as anzahl\r\nFROM bestellungen natural join bestellpos\r\nGROUP BY bestellungen.b_id\r\nORDER BY Anzahl desc'),
(4900, 328, 7, 16, 'SELECT k_name, s_bezeichner, count(artikel.s_id) as Anzahl\r\nFROM kunden natural join bestellungen natural join bestellpos natural join artikel natural join segmente\r\nGROUP BY k_name, artikel.s_id\r\nORDER BY k_name, s_bezeichner'),
(4901, 329, 2, 16, 'SELECT min(pl_einzelpreis) as MIN, max(pl_einzelpreis) as MAX, avg(pl_einzelpreis) as Durchn\r\nFROM preisliste2006'),
(4902, 329, 8, 16, 'SELECT s_bezeichner, min(pl_einzelpreis) as MIN, max(pl_einzelpreis) as MAX, avg(pl_einzelpreis) as Durchn\r\nFROM preisliste2006 natural join artikel natural join segmente\r\nGROUP BY s_bezeichner');
INSERT INTO `loesungen` (`l_id`, `u_id`, `auf_id`, `s_id`, `l_text`) VALUES
(4903, 329, 14, 16, 'SELECT k_name as Kunde, sum(pl_einzelpreis*bp_anzahl) as Summe\r\nFROM preisliste2006 natural join artikel natural join bestellpos natural join bestellungen natural join kunden\r\nGROUP BY k_id'),
(4904, 329, 7, 16, 'SELECT k_name, s_bezeichner, count(s_bezeichner)\r\nFROM kunden natural join bestellungen natural join bestellpos natural join artikel natural join segmente\r\nGROUP BY k_name, s_bezeichner\r\nORDER BY k_name asc, s_bezeichner asc'),
(4905, 329, 44, 16, 'SELECT s_id, s_bezeichner, count(a_bezeichner) as Anzahl\r\nFROM artikel natural join segmente\r\nGROUP BY s_bezeichner\r\nHAVING Anzahl > 5'),
(4906, 324, 14, 16, 'SELECT k_name, SUM(pl_einzelpreis*bp_anzahl) AS Summe\r\nFROM kunden K, bestellungen B, bestellpos Bp, artikel A, preisliste2006 Pl\r\nWHERE K.k_id=B.k_id AND B.b_id=Bp.b_id AND A.a_id=Bp.a_id AND A.a_id=Pl.a_id\r\nGROUP BY K.k_id'),
(4914, 322, 14, 16, 'select k_name, sum(pl_einzelpreis*bp_anzahl) as summe \r\nfrom kunden inner join bestellungen using (k_id)\r\ninner join bestellpos using (b_id)\r\ninner join preisliste2006 using (a_id)\r\ngroup by kunden.k_id '),
(4907, 324, 15, 16, 'SELECT a_bezeichner, COUNT(Bp.bp_anzahl) AS Bestellungen, SUM(Bp.bp_anzahl) AS Anzahl\r\nFROM bestellpos Bp, artikel A\r\nWHERE A.a_id=Bp.a_id\r\nGROUP BY A.a_id\r\nORDER BY Bestellungen'),
(4908, 324, 25, 16, 'select sum(bp_anzahl*pl_einzelpreis)as Gesamtwert from (preisliste2006 inner join bestellpos on preisliste2006.a_id=bestellpos.a_id) inner join bestellungen on bestellungen.b_id=bestellpos.b_id where b_datum < ''2006-11-1'''),
