(7396, 409, 8, 16, 'SELECT s.s_bezeichner AS b, min(pl_einzelpreis) AS min, max(pl_einzelpreis) AS max, ROUND(avg(pl_einzelpreis) AS ''Durchschnittspreis''), 2)\r\nFROM segmente INNER JOIN artikel ON artikel.s_id=segmente.s_id INNER JOIN preisliste2006 ON preisliste2006.a_id=artikel.a_id \r\nGROUP BY s.s_id'),
(7397, 413, 8, 16, 'SELECT s.s_bezeichner AS SEGMENTE, min(pl.pl_einzelpreis) AS ''niedrigster_Preis'', max(pl.pl_einzelpreis) AS ''hochster_Preis'', avg(pl.pl_einzelpreis) AS ''durchschnittlicher_Preis''\r\nFROM preisliste2006 AS pl inner Join artikel AS a using(a_id) inner JOIN segmente AS s using(s_id)\r\nGROUP BY s.s_bezeichner;\r\n\r\n'),
(7398, 412, 44, 16, 'SELECT s_id AS Segment-ID,s.s_bezeichner AS Segment-Bezeichnung, COUNT(a.a_id) AS Anzahl \r\nFROM artikel AS a INNER JOIN segmente AS s USING(s_id)\r\nGROUP BY s_id having Anzahl >5;'),
(7399, 408, 8, 16, 'SELECT s.s_bezeichner AS Segmente, MIN(pl.pl_einzelpreis) AS ''kleinster_Preis'', MAX(pl.pl_einzelpreis) AS ''größter_Preis'', ROUND(AVG(pl.pl_einzelpreis),2) AS ''durchschnittlicher_Preis''\r\nFROM segmente AS s INNER JOIN artikel AS a USING(s_id) INNER JOIN preisliste2006 AS pl USING(a_id)\r\nGROUP BY s.s_bezeichner;'),
(7400, 404, 8, 16, 'SELECT MIN(pl.pl_einzelpreis) AS minimaler_preis,MAX(pl.pl_einzelpreis) AS maximaler_preis,ROUND(AVG(pl.pl_einzelpreis),2) AS durchschnittlicher_preis\r\nFROM preisliste2006 AS pl INNER JOIN artikel USING(a_id) INNER JOIN segmente USING(s_id) \r\nGROUP BY s_bezeichner;'),
(7401, 417, 8, 16, 'SELECT s.s_bezeichner AS Segment, MIN(pl.pl_einzelpreis)AS Mindestpreis, MAX(pl.pl_einzelpreis)AS Höchstpreis, ROUND(SUM(pl.pl_einzelpreis)/COUNT(a.a_id),2)AS Durchschnittspreis\r\nFROM artikel AS a INNER JOIN preisliste2006 AS pl USING(a_id) INNER JOIN segmente as s USING(s_id)\r\nGROUP BY s.s_id'),
