(7414, 403, 15, 16, 'SELECT a.a_id AS Artikelidentifikation, a.a_bezeichner AS Artikelbezeichner, COUNT(b.b_id) AS Bestellanzahl, SUM(bp.bp_anzahl) AS Gesamtmenge\r\nFROM bestellungen AS b INNER JOIN bestellpos AS bp USING(b_id) INNER JOIN artikel AS a USING(a_id)\r\nGROUP BY a.a_id\r\nORDER BY Bestellanzahl DESC; \r\n'),
(7493, 409, 7, 16, 'SELECT k_name, s_bezeichner, COUNT(artikel.a_id) AS ''Segementanzahl''\r\nFROM kunden AS k INNER JOIN bestellungen AS b USING(k_id) INNER JOIN bestellpos AS bp USING(b_id)INNER JOIN artikel USING(a_id) INNER JOIN segmente USING(s_id)\r\nGROUP BY k_name, artikel.s_id\r\nORDER BY k_name ASC, segmente.s_bezeichner ASC;'),
(7415, 403, 25, 16, 'SELECT SUM(bp.bp_anzahl*pl.pl_einzelpreis) AS Gesamtwert\r\nFROM bestellungen AS b INNER JOIN bestellpos AS bp USING(b_id) INNER JOIN artikel AS a USING(a_id) INNER JOIN preisliste2006 AS pl USING(a_id)\r\nWHERE b.b_datum<''2006-11-1''\r\n'),
