(7452, 409, 2, 16, 'SELECT min(pl_einzelpreis) AS Niedrigste, max(pl_einzelpreis) AS Höchste, ROUND(avg(pl_einzelpreis) AS Schnitt,2)\r\nFROM preisliste2006'),
(7453, 408, 2, 16, 'SELECT min(pl.pl_einzelpreis) AS ''niegrigster_Preis'', max(pl.pl_einzelpreis) AS ''höchster_Preis'', ROUND(avg(pl.pl_einzelpreis),2) AS ''Durchschnittspreis''\r\nFROM preisliste2006 AS pl;'),
(7454, 408, 14, 16, 'SELECT k.k_name AS ''Kundenname'', (bp.bp_anzahl*pl.pl_einzelpreis) AS ''Summe''\r\nFROM kunden AS k INNER JOIN bestellungen AS b USING(k_id) INNER JOIN bestellpos AS bp USING(b_id) INNER JOIN artikel AS a USING(a_id) INNER JOIN preisliste2006 AS pl USING(a_id)\r\nGROUP BY k.k_id;'),
(7455, 409, 14, 16, 'SELECT k_name AS Kundenname, sum(bp_anzahl*pl_einzelpreis) AS Summe\r\nFROM kunden AS k INNER JOIN bestellungen AS b ON k.k_id=b.b_id INNER JOIN bestellpos AS bp on bp.bp_id=b.b_id INNER JOIN preisliste2006 on preisliste2006.a_id=bp.a_id\r\nGROUP BY kunden.k_id '),
(7456, 408, 15, 16, 'SELECT a.a_bezeichner AS ''Artikelbezeichnung'', COUNT(bp.bp_anzahl) AS ''Bestellungen'', SUM(bp.bp_anzahl)\r\nFROM artikel AS a INNER JOIN bestellpos AS bp USING(a_id)\r\nGROUP BY a.a_id\r\nORDER BY a.a_bezeichner DESC;'),
(7457, 408, 25, 16, 'SELECT SUM(bp.bp_anzahl*pl.pl_einzelpreis) AS ''Gesamtwert''\r\nFROM preisliste2006 AS pl INNER JOIN artikel AS a USING(a_id) INNER JOIN bestellpos AS bp USING(a_id) INNER JOIN bestellungen AS b USING(b_id)\r\nWHERE b.b_datum< ''2006-11-1'';'),
(7458, 408, 3, 16, 'SELECT b.b_id, b.b_datum AS ''Bestelldatum'', COUNT(bp.bp_id) AS ''Anzahl_Bestellpositionen'' \r\nFROM bestellungen AS b INNER JOIN bestellpos AS bp USING(b_id)\r\nGROUP BY b.b_id\r\nORDER BY bp.bp_id DESC;'),
(7459, 409, 15, 16, 'SELECT artikel.a_bezeichner, COUNT(bestellpos.bp_anzahl) as Bestellungen, sum(bestellpos.bp_anzahl) as Stückzahl \r\nFROM bestellpos INNER JOIN artikel ON artikel.a_id=bestellpos.a_id \r\nGROUP BY artikel.a_id \r\nORDER BY Bestellungen DESC;'),
(7460, 408, 7, 16, 'SELECT k.k_name, s.s_bezeichner, COUNT(a.s_id) AS ''Segmentanzahl''\r\nFROM segmente AS s INNER JOIN artikel AS a USING(s_id) INNER JOIN bestellpos AS bp USING(a_id) INNER JOIN bestellungen AS b USING(b_id) INNER JOIN kunden AS k USING(k_id)\r\nGROUP BY k.k_name, a.s_id\r\nORDER BY k.k_name, s.s_bezeichner'),
(7461, 409, 25, 16, 'SELECT SUM(bp_anzahl*pl_einzelpreis) AS Gesamtwert\r\nFROM preisliste2006 INNER JOIN bestellpos ON preisliste2006.a_id=bestellpos.a_id INNER JOIN bestellungen on bestellungen.b_id=bestellpos.b_id \r\nWHERE b_datum < ''2006-11-1'';'),
(7462, 416, 14, 16, 'SELECT k.k_name, SUM(pl.pl_einzelpreis*bp.bp_anzahl) AS summe \r\nFROM kunden AS k INNER JOIN bestellungen as b USING (k_id) INNER JOIN bestellpos AS bp USING (b_id) INNER JOIN preisliste2006 AS pl USING(a_id) \r\nGROUP BY k.k_id'),
(7463, 416, 15, 16, 'SELECT a.a_bezeichner, COUNT(bp.bp_anzahl) AS Anzahl, SUM(bp.bp_anzahl) as Gesammtmenge \r\nFROM bestellpos AS bp INNER JOIN artikel AS a USING (a_id)\r\nGROUP BY a.a_id'),
(7464, 416, 25, 16, 'SELECT SUM(bp.bp_anzahl*pl.pl_einzelpreis) AS SUMME\r\nFROM preisliste2006 AS pl INNER JOIN bestellpos AS bp USING(a_id) INNER JOIN bestellungen AS b USING(b_id)\r\nWHERE b.b_datum < ''2006-11-1'''),
(7465, 407, 14, 16, 'SELECT k.k_name AS Kundenname, SUM(bp.bp_anzahl*pl.pl_einzelpreis) AS Gesammtsumme\r\nFROM kunden AS k INNER JOIN bestellungen USING(k_id) INNER JOIN bestellpos AS bp USING(b_id) INNER JOIN artikel USING(a_id) INNER JOIN preisliste2006 AS pl USING(a_id)\r\nGROUP BY k.k_id'),
(7466, 416, 3, 16, 'SELECT b.b_id, COUNT(bp.bp_id) AS Anzahl \r\nFROM bestellpos AS bp INNER JOIN bestellungen AS b USING(b_id)\r\nGROUP BY b.b_id\r\n'),
(7467, 416, 7, 16, 'SELECT k.k_name, s.s_bezeichner, COUNT(a.s_id) AS Segmentanzahl \r\nFROM kunden AS k INNER JOIN bestellungen AS b USING(k_id) INNER JOIN bestellpos AS bp USING(b_id) INNER JOIN artikel AS a USING(a_id) INNER JOIN segmente AS s USING(s_id)\r\nGROUP BY k.k_name, a.s_id \r\nORDER BY k.k_name ASC, s.s_bezeichner ASC'),
(7468, 407, 15, 16, 'SELECT a.a_bezeichner AS Artikelname, COUNT(bp.a_id) AS Anzahl_der_Bestellvorgänge, SUM(bp.bp_anzahl) As Gesammtmenge\r\nFROM bestellpos AS bp INNER JOIN artikel AS a USING(a_id)\r\nGROUP BY a.a_id\r\nORDER BY Anzahl_der_Bestellvorgänge DESC;'),
(7469, 407, 25, 16, 'SELECT SUM(bp.bp_anzahl*pl.pl_einzelpreis) AS Gesamtwert_aller_Bestellungen\r\nFROM bestellungen AS b INNER JOIN bestellpos AS bp USING(b_id) INNER JOIN artikel USING(a_id) INNER JOIN preisliste2006 AS pl USING(a_id)\r\nWHERE b.b_datum < ''2006-11-1'''),
(7470, 407, 44, 16, 'SELECT COUNT(a.a_id) as Artikelanzahl, s.s_bezeichner as Segment\r\nFROM artikel as a INNER JOIN segmente as s USING(s_id)\r\nGROUP BY s.s_id HAVING Artikelanzahl >5'),
(7471, 407, 3, 16, 'SELECT b.b_id AS Bestell_ID, b.b_datum AS Bestelldatum, COUNT(bp.bp_id) Anzahl\r\nFROM bestellungen AS b INNER JOIN bestellpos AS bp USING(b_id)\r\nGROUP BY b.b_id\r\nORDER BY Anzahl DESC;'),
(7472, 407, 7, 16, 'SELECT k.k_name AS Kundenname, s.s_bezeichner AS Segmentbezeichner, COUNT(s.s_id) AS Segmenteanzahl\r\nFROM kunden AS k INNER JOIN bestellungen AS b USING(k_id) INNER JOIN bestellpos USING(b_id) INNER JOIN artikel as a USING(a_id) INNER JOIN segmente AS s USING(s_id)\r\nGROUP BY k.k_name, s.s_id\r\nORDER BY k.k_name ASC, Segmentbezeichner ASC;'),
(7473, 410, 12, 16, 'SELECT b.b_datum AS Bestelldatum, count(b_id) AS Anzahl\r\nFROM bestellungen AS b\r\nGROUP BY b.b_datum\r\nHAVING Anzahl > 1\r\nORDER BY Anzahl DESC;'),
(7474, 410, 2, 16, 'SELECT MIN(pl.pl_einzelpreis) AS Niedrigste, MAX(pl.pl_einzelpreis) AS Hoechste, ROUND(AVG(pl.pl_einzelpreis),2) AS Schnitt\r\nFROM preisliste2006 AS pl'),
(7475, 410, 14, 16, 'SELECT k_name, SUM(pl_einzelpreis*bp_anzahl) AS summe\r\nFROM (((kunden inner join bestellungen ON kunden.k_id=bestellungen.k_id) INNER JOIN bestellpos ON bestellpos.b_id=bestellungen.b_id) INNER JOIN preisliste2006 ON preisliste2006.a_id=bestellpos.a_id)\r\nGROUP BY kunden.k_id\r\nORDER BY k_name ASC;'),
(7476, 410, 15, 16, 'SELECT a_bezeichner, COUNT(bestellpos.bp_anzahl) AS Bestellungen, SUM(bestellpos.bp_anzahl) AS Stückzahl\r\nFROM bestellpos INNER JOIN artikel ON artikel.a_id=bestellpos.a_id\r\nGROUP BY artikel.a_id\r\nORDER BY Bestellungen DESC;'),
(7477, 410, 25, 16, 'SELECT sum(bp_anzahl*pl_einzelpreis)as Gesamtwert \r\nFROM (preisliste2006 inner join bestellpos on preisliste2006.a_id=bestellpos.a_id) inner join bestellungen on bestellungen.b_id=bestellpos.b_id\r\nWHERE b_datum < ''2006-11-1'';'),
(7478, 410, 3, 16, 'SELECT bestellungen.b_id,b_datum,count(bp_id) as Anzahl\r\nFROM bestellpos inner join bestellungen on bestellungen.b_id=bestellpos.b_id group by bestellungen.b_id\r\nORDER BY Anzahl desc'),
(7479, 410, 7, 16, 'SELECT k_name,s_bezeichner, count(artikel.s_id) as Segmentanzahl\r\nFROM kunden inner join bestellungen using (k_id) inner join bestellpos using (b_id) inner join artikel using (a_id) inner join segmente using (s_id)\r\nGROUP BY k_name,artikel.s_id\r\nORDER BY k_name, s_bezeichner'),
(7480, 409, 3, 16, 'SELECT bestellungen.b_id, COUNT(bestellpos.bp_id) AS Bestellpositionen_je_Bestellung\r\nFROM bestellungen AS b INNER JOIN bestellpos AS bp USING b.b_id=bp.b_id\r\nGROUP BY b.b_id\r\nORDER BY Bestellpositionen_je_Bestellung desc;\r\n'),
(7481, 404, 3, 16, 'SELECT COUNT(bp.bp_id) AS ''Anzahl Bestellpositionen'', b.b_id AS Bestellnummer\r\nFROM bestellpos AS bp INNER JOIN bestellungen AS b USING(b_id)\r\nGROUP BY b.b_id;'),
(7482, 413, 44, 16, 'SELECT a.s_id, COUNT(a_id) AS Artikelanzahl\r\nFROM artikel AS a\r\nGROUP BY a.s_id HAVING Artikelanzahl >5'),
(7483, 404, 7, 16, 'SELECT k.k_name AS kunde,s.s_id,s.s_bezeichner,COUNT(b.b_id) AS anzahl\r\nFROM kunden AS k INNER JOIN bestellungen AS b USING(k_id) INNER JOIN bestellpos USING(b_id) INNER JOIN artikel USING(a_id) INNER JOIN segmente AS s USING(s_id)\r\nGROUP BY k.k_name,s.s_id\r\nORDER BY k.k_name ASC,s.s_bezeichner ASC;'),
(7484, 404, 25, 16, 'SELECT SUM(bp_anzahl*pl_einzelpreis) AS Gesamtwert \r\nFROM preisliste2006 INNER JOIN bestellpos USING(a_id) INNER JOIN bestellungen USING(b_id) \r\nWHERE b_datum < ''2006-11-1'''),
(7485, 413, 7, 16, 'SELECT k.k_name, s.s_bezeichner, COUNT(a.s_id) AS Segmentanzahl\r\nFROM Kunden AS k INNER JOIN bestellungen AS b USING(k_id) INNER JOIN bestellpos AS bp USING(b_id) INNER JOIN  artikel AS a USING(a_id) INNER JOIN segmente AS s USING(s_id)\r\nGROUP BY k.k_name, a.s_id\r\nORDER BY k.k_name, s.s_bezeichner\r\n'),
(7486, 405, 2, 16, 'SELECT MIN(pl.pl_einzelpreis) AS niedrigster, MAX(pl.pl_einzelpreis) AS höchster, AVG(pl.pl_einzelpreis) AS durchschnittlicher\r\nFROM preisliste2006 AS pl;'),
(7487, 405, 14, 16, 'SELECT k.k_name, SUM(bp.bp_anzahl) AS Gesamtsumme\r\nFROM kunden AS k INNER JOIN bestellungen USING(k_id) INNER JOIN bestellpos AS bp USING(b_id)\r\nGROUP BY k.k_id\r\nORDER BY k.k_id ASC;'),
(7488, 405, 15, 16, 'SELECT a.a_bezeichner, COUNT(bp.bp_anzahl) AS Anzahl_der_Bestellvorgänge, SUM(bp.bp_anzahl) AS Menge_der_Bestellungen\r\nFROM bestellpos AS bp INNER JOIN artikel AS a USING(a_id)\r\nGROUP BY a.a_id\r\nORDER BY a.a_id ASC;\r\n'),
(7489, 412, 15, 16, 'SELECT a.a_bezeichner, COUNT(b_id) AS Anzahl, SUM(bp_anzahl) AS Stückzahl\r\nFROM bestellungen INNER JOIN bestellpos USING(b_id) INNER JOIN artikel AS a USING(a_id)\r\nGROUP BY a.a_id;\r\n'),
(7490, 412, 25, 16, 'SELECT b.b_id, SUM(pl.pl_einzelpreis*(1+s.s_mwst)*bp.bp_anzahl) AS Gesamtwert\r\nFROM bestellungen AS b INNER JOIN bestellpos AS bp USING(b_id) INNER JOIN artikel AS a USING(a_id) INNER JOIN preisliste2006 AS pl USING(a_id) INNER JOIN segmente AS s USING(s_id)\r\nGROUP BY b.b_datum < 2006-11-1;'),
(7491, 405, 25, 16, 'SELECT SUM(bp.bp_anzahl * pl.pl_einzelpreis)AS Gesamtwert \r\nFROM preisliste2006 AS pl INNER JOIN bestellpos AS bp USING(a_id) INNER JOIN bestellungen AS b USING(b_id) \r\nWHERE b.b_datum < ''2006-11-1'';'),
(7494, 412, 3, 16, 'SELECT b.b_id, k.k_id, COUNT(bp.bp_id) AS Anzahl\r\nFROM kunden AS k INNER JOIN bestellungen AS b USING(k_id) INNER JOIN bestellpos AS bp USING(b_id)\r\nGROUP by bp.b_id;'),
(7495, 412, 7, 16, 'SELECT CONCAT(k.k_vname, '' '',k.k_name) AS Name,s.s_bezeichner, COUNT(bp.a_id) \r\nFROM kunden AS k INNER JOIN bestellungen AS b USING(k_id)INNER JOIN bestellpos AS bp USING(b_id) INNER JOIN artikel AS a USING(a_id) INNER JOIN segmente AS s USING(s_id)\r\nGROUP BY k.k_id, a.s_id\r\nORDER BY k.k_name ASC, s.s_bezeichner ASC;'),
(7496, 415, 12, 16, 'SELECT b.b_datum AS datum, count( b_id) AS artikelanzahl\r\nFROM bestellungen AS b\r\nGROUP BY b.b_datum\r\nHAVING artikelanzahl > 1\r\nORDER BY artikelanzahl'),
(7497, 415, 2, 16, 'SELECT MIN(p.pl_einzelpreis) AS minimum, AVG(p.pl_einzelpreis) AS mittelwert, MAX(p.pl_einzelpreis) AS maximum\r\nFROM preisliste2006 AS p\r\n'),
(7498, 415, 14, 16, 'SELECT k.k_name AS kundenname, count(b_id) AS bestellungen\r\nFROM kunden AS k INNER JOIN bestellungen AS b\r\nGROUP BY k_name'),
(7499, 415, 15, 16, 'SELECT a.a_bezeichner, count(b.bp_anzahl) AS anzahl, sum(b.bp_anzahl) AS stückzahl\r\nFROM bestellpos AS b INNER JOIN artikel AS a on artikel.a_id=bestellpos.a_id\r\nGROUP BY a.a_id'),
(7500, 405, 7, 16, 'Select k_name, s_bezeichner, count(s_id) as Segmentanzahl \r\nFrom kunden inner join bestellungen using (k_id) inner join bestellpos using (b_id) inner join artikel using (a_id) inner join segmente using (s_id) \r\nGroup BY k_name,s_id \r\norder by k_name, s_bezeichner;'),
(7501, 415, 3, 16, 'SELECT bestellungen.b_id, count(bestellpos.bp_id) AS anzahl\r\nFROM bestellpos INNER JOIN bestellungen\r\nGROUP BY bestellungen.b_id'),
(7502, 417, 12, 16, 'SELECT b.b_datum AS Bestelldatum, COUNT(b.b_id)AS Anzahl_Bestellungen\r\nFROM bestellungen AS b \r\nGROUP BY Bestelldatum HAVING Anzahl_Bestellungen > 1\r\nORDER BY Anzahl_Bestellungen DESC;');
