(7507, 417, 3, 16, 'SELECT (b.b_id)AS Bestellung, COUNT(bp.bp_id) Bestellpositionen\r\nFROM bestellungen AS b INNER JOIN bestellpos AS bp USING(b_id)\r\nGROUP BY b.b_id\r\nORDER BY Bestellpositionen DESC'),
(7508, 417, 7, 16, 'SELECT (k.k_name)AS Kunde, (s.s_bezeichner)AS Segment, COUNT(bp.bp_anzahl) Segmentanzahl\r\nFROM kunden AS k INNER JOIN bestellungen AS b USING(k_id) INNER JOIN bestellpos AS bp USING(b_id) INNER JOIN artikel AS a USING(a_id) INNER JOIN segmente AS s USING(s_id)\r\nGROUP BY k.k_name, s.s_bezeichner\r\nORDER BY k.k_name, s.s_bezeichner\r\n'),
(7510, 353, 29, 14, 'select s_bezeichner, a_bezeichner from artikel right join segmente on segmente.s_id=artikel.s_id order by s_bezeichner, a_bezeichner'),
(7511, 428, 31, 15, 'select concat(k_vname," ",k_name, " aus ",k_ort)as Kunde,\r\nconcat(left(k_vname,1), left(k_name,1)) as Initialen from kunden \r\norder by Initialen, k_ort'),
(7512, 428, 30, 15, 'select a_bezeichner, pl_einzelpreis, pl_rabattsatz,(pl_einzelpreis*(1-pl_rabattsatz)) as ep_rabatt\r\nfrom artikel inner join preisliste2006 on artikel.a_id=preisliste2006.a_id\r\norder by ep_rabatt desc'),
(7513, 426, 30, 15, 'SELECT a_bezeichner, pl_einzelpreis, pl_rabattsatz,(pl_einzelpreis*(1-pl_rabattsatz)) as ep_rabatt \r\nFROM artikel inner join preisliste2006 USING(a_id)\r\nORDER BY ep_rabatt desc'),
(7514, 426, 31, 15, 'SELECT concat(k_vname," ",k_name, " aus ",k_ort)as Kunde, concat(left(k_vname,1), left(k_name,1)) as Initialen FROM kunden \r\nORDER BY Initialen, k_ort'),
(7515, 426, 33, 15, 'SELECT a_bezeichner, s_mwst, pl_einzelpreis as PohneMwst, (pl_einzelpreis * (1+s_mwst)) as PmMwst\r\nFROM (artikel inner join segmente using(s_id)) inner join preisliste2006 using(a_id)\r\nORDER BY s_mwst, pl_einzelpreis\r\n'),
(7516, 426, 35, 15, 'SELECT bp_id, (bp_anzahl * pl_einzelpreis) as "gesamt"\r\nFROM bestellpos inner join preisliste2006 using(a_id)\r\nORDER BY gesamt desc limit 0,3'),
(7517, 414, 2, 16, 'SELECT min(pl_einzelpreis) AS Niedrigste, MAX(pl_einzelpreis) AS Hoechste, AVG(pl_einzelpreis) AS Schnitt \r\nFROM preisliste2006'),
(7518, 414, 14, 16, 'SELECT k_name, -- Kundenname SUM(pl_einzelpreis*bp_anzahl) AS summe -- Summe des Positionsbetrages \r\nFROM (((kunden INNER JOIN bestellungen ON kunden.k_id=bestellungen.k_id) INNER JOIN bestellpos ON bestellpos.b_id=bestellungen.b_id) INNER JOIN preisliste2006 ON preisliste2006.a_id=bestellpos.a_id) \r\nGROUP BY kunden.k_id; '),
(7519, 414, 15, 16, 'SELECT a_bezeichner, count(bestellpos.bp_anzahl) AS Bestellungen, SUM(bestellpos.bp_anzahl) AS Stückzahl \r\nFROM bestellpos INNER JOIN artikel ON artikel.a_id=bestellpos.a_id \r\nGROUP BY artikel.a_id \r\nORDER BY Bestellungen DESC;'),
(7520, 414, 25, 16, 'SELECT SUM(bp_anzahl*pl_einzelpreis)AS Gesamtwert \r\nFROM (preisliste2006 INNER JOIN bestellpos ON preisliste2006.a_id=bestellpos.a_id) INNER JOIN bestellungen ON bestellungen.b_id=bestellpos.b_id \r\nWHERE b_datum < ''2006-11-1'';'),
(7521, 414, 44, 16, 'SELECT s_id, COUNT(a_id)AS Artikelzahl -- Auswahl der Datenfelder \r\nFROM artikel GROUP BY s_id -- Gruppierung nach s_id \r\nHAVING Artikelzahl >5;'),
(7522, 414, 3, 16, 'SELECT bestellungen.b_id,b_datum,count(bp_id) AS Anzahl \r\nFROM bestellpos INNER JOIN bestellungen ON bestellungen.b_id=bestellpos.b_id \r\nGROUP BY bestellungen.b_id \r\nORDER BY Anzahl DESC;'),
(7523, 411, 35, 15, 'SELECT\r\nFROM \r\nWHERE\r\nORDER BY'),
(7524, 411, 34, 15, 'SELECT\r\nFROM \r\nWHERE\r\nORDER BY'),
