(7526, 411, 2, 16, 'select min(pl_einzelpreis) as Niedrigste, max(pl_einzelpreis) as Hoechste, avg(pl_einzelpreis) as Schnitt \r\nfrom preisliste2006'),
(7527, 411, 8, 16, 'select s_bezeichner, min(pl_einzelpreis)as min, max(pl_einzelpreis)as max, avg(pl_einzelpreis) as avg \r\nfrom (segmente inner join artikel on artikel.s_id=segmente.s_id) inner join preisliste2006 on preisliste2006.a_id=artikel.a_id group by s_bezeichner'),
(7528, 411, 14, 16, 'select k_name, -- Kundenname sum(pl_einzelpreis*bp_anzahl) as summe -- Summe des Positionsbetrages \r\nfrom (((kunden inner join bestellungen on kunden.k_id=bestellungen.k_id) inner join bestellpos on bestellpos.b_id=bestellungen.b_id) inner join preisliste2006 on preisliste2006.a_id=bestellpos.a_id) \r\ngroup by kunden.k_id '),
