(7530, 411, 44, 16, 'select s_id, count(a_id)as Artikelzahl -- Auswahl der Datenfelder \r\nfrom artikel \r\ngroup by s_id -- Gruppierung nach s_id having Artikelzahl >5'),
(9978, 518, 35, 15, 'SELECT bp_id, (bp_anzahl*pl_einzelpreis) AS gesamt \r\nFROM bestellpos INNER JOIN preisliste2006 ON bestellpos.a_id=preisliste2006.a_id \r\nORDER BY gesamt DESC \r\nLIMIT 0,3; #bei 0 starten, wenn 5 dann ab 6'),
(7532, 411, 7, 16, 'select k_name,s_bezeichner, count(artikel.s_id) as Segmentanzahl \r\nfrom kunden inner join bestellungen using (k_id) inner join bestellpos using (b_id) inner join artikel using (a_id) inner join segmente using (s_id) \r\ngroup by k_name,artikel.s_id \r\norder by k_name, s_bezeichner'),
