(8795, 479, 25, 16, 'SELECT SUM(bp.bp_anzahl*pl.pl_einzelpreis) AS Gesamtwert\r\nFROM bestellpos AS bp INNER JOIN preisliste2006 AS pl USING(a_id)\r\n   INNER JOIN bestellungen AS b USING(b_id)\r\nWHERE b_datum < "2006-11-1"\r\n'),
(8796, 445, 25, 16, 'SELECT SUM(pl.pl_einzelpreis*bp.bp_anzahl) AS Gesamtwert\r\nFROM preisliste2006 AS pl\r\n   INNER JOIN bestellpos AS bp ON pl.a_id = bp.a_id\r\n   INNER JOIN bestellungen AS b on bp.b_id = b.b_id\r\nWHERE b.b_datum < "2006-11-1";'),
(8797, 452, 15, 16, 'SELECT a.a_id AS Artikelnummer, a.a_bezeichner AS Artikelname, COUNT(b.b_id), SUM(bp.bp_anzahl)\r\nFROM artikel AS a\r\n     INNER JOIN bestellpos AS bp USING(a_id)\r\n     INNER JOIN bestellungen AS b USING(b_id)\r\nGROUP BY Artikelnummer; '),
(8798, 477, 3, 16, 'SELECT b.b_id, COUNT(bp.bp_anzahl) AS Bestellpositionen, b.b_datum\r\nFROM bestellpos AS bp\r\n INNER JOIN bestellungen AS b USING(b_id)\r\nGROUP BY b.b_id;'),
(8800, 480, 25, 16, 'SELECT SUM(pl.pl_einzelpreis*bp.bp_anzahl) AS Gesamtwert\r\nFROM preisliste2006 AS pl\r\n INNER JOIN artikel AS a USING (a_id)\r\n INNER JOIN bestellpos AS bp USING (a_id)\r\n INNER JOIN bestellungen AS b USING (b_id)\r\nWHERE b.b_datum < "2006-11-1";\r\n'),
(8801, 453, 3, 16, 'SELECT b.b_id AS Bestellungen,b.b_datum AS Datum, COUNT(bp.bp_id) AS "BP-Anzahl"\r\nFROM bestellungen AS b\r\n   INNER JOIN bestellpos AS bp USING (b_id)\r\nGROUP BY Bestellungen\r\n'),
