(8810, 477, 7, 16, 'SELECT k.k_name AS Kundenname, s.s_bezeichner AS Segmentbezeichner, COUNT(a.s_id) AS Segmentanzahl\r\nFROM kunden AS k\r\n INNER JOIN bestellungen AS b USING(k_id)\r\n INNER JOIN bestellpos AS bp USING(b_id)\r\n INNER JOIN artikel AS a USING(a_id)\r\n INNER JOIN segmente AS s USING(s_id)\r\nGROUP BY Segmentbezeichner, Kundenname\r\nORDER BY Kundenname ASC, Segmentbezeichner ASC ;'),
(8812, 451, 44, 16, 'SELECT COUNT(a.a_id) AS anzahl,s.s_id,s.s_bezeichner\r\nFROM artikel As a \r\nINNER JOIN segmente as s USING (s_id)\r\nGROUP BY s.s_id\r\nHAVING anzahl >= 6\r\n'),
