(8819, 452, 44, 16, 'SELECT s.s_id AS Segment, COUNT(a.a_id) AS Artikelanzahl\r\nFROM segmente AS s \r\n     INNER JOIN artikel AS a USING(s_id)\r\nGROUP BY Segment\r\nHAVING Artikelanzahl > 5;'),
(8820, 441, 7, 16, 'SELECT CONCAT(LEFT(k.k_vname,1),". ",k_name) AS Kundenname, s.s_bezeichner AS Segmentebezeichner,COUNT(a.s_id) AS Segmenteanzahl\r\nFROM kunden AS k\r\n  INNER JOIN bestellungen AS b USING(k_id)\r\n  INNER JOIN bestellpos AS bp USING(b_id)\r\n  INNER JOIN artikel AS a USING(a_id)\r\n  INNER JOIN segmente AS s USING(s_id)\r\nGROUP BY Kundenname, Segmentebezeichner\r\nORDER BY Kundenname ASC, Segmentebezeichner ASC;'),
(8821, 442, 7, 16, 'SELECT k.k_name AS Kundenname, s.s_bezeichner AS Segmentname, COUNT(a.s_id) AS Segmentanzahl\r\nFROM kunden As k \r\n INNER JOIN bestellungen USING(k_id)\r\n INNER JOIN bestellpos USING(b_id)\r\n INNER JOIN artikel AS a USING(a_id) \r\n INNER JOIN segmente AS s USING(s_id)\r\nGROUP BY Kundenname, Segmentname\r\nORDER BY Kundenname ASC, Segmentname ASC;\r\n'),
(8822, 450, 7, 16, 'SELECT COUNT(artikel.a_id) AS AnzahlBestellungen, kunden.k_name AS Name, segmente.s_bezeichner AS Segment\r\nFROM (((kunden INNER JOIN bestellungen USING(k_id)) INNER JOIN bestellpos USING(b_id))\r\n   INNER JOIN artikel USING(a_id)) INNER JOIN segmente USING(s_id)\r\nGROUP BY kunden.k_name, artikel.s_id\r\nORDER BY kunden.k_name ASC, segmente.s_bezeichner ASC;'),
(8823, 443, 25, 16, 'SELECT SUM(bp.bp_anzahl*pl.pl_einzelpreis) AS Gesamtwert\r\nFROM bestellungen AS b\r\n  INNER JOIN bestellpos AS bp USING (b_id)\r\n  INNER JOIN preisliste2006 AS pl USING (a_id)\r\nWHERE b.b_datum < "2006-11-1"'),
(8824, 447, 7, 16, 'SELECT k.k_name AS Kunde, s.s_bezeichner, COUNT(a.s_id) AS Segmentanzahl\r\nFROM kunden AS k INNER JOIN bestellungen USING(k_id) INNER JOIN bestellpos USING(b_id) INNER JOIN artikel AS a USING(a_id) INNER JOIN segmente AS s USING(s_id)\r\nGROUP BY Kunde, s.s_bezeichner\r\nORDER BY Kunde ASC, s.s_bezeichner ASC;'),
(8825, 449, 7, 16, 'SELECT k.k_vname, k.k_name, s.s_bezeichner, COUNT(s_id) AS Segmentanzahl\r\nFROM kunden AS k \r\n  INNER JOIN bestellungen AS b USING(k_id)\r\n  INNER JOIN bestellpos AS bp USING(b_id)\r\n  INNER JOIN artikel AS a USING(a_id) \r\n  INNER JOIN segmente AS s USING(s_id) \r\nGROUP BY k.k_name, s_id\r\nORDER BY k.k_name ASC, s.s_bezeichner ASC; '),
(8826, 479, 3, 16, 'SELECT b.b_id AS Bestellung, b.b_datum AS Datum, COUNT(bp.bp_id) AS Bestellpositionenanzahl \r\nFROM bestellungen AS b INNER JOIN bestellpos As bp USING(b_id)\r\nGROUP BY b.b_id\r\nORDER BY Bestellung ASC;'),
(8827, 479, 7, 16, 'SELECT k.k_name AS Kundenname, s.s_bezeichner AS Segmentbezeichner, COUNT(a.s_id) AS Segmentanzahl\r\nFROM kunden AS k INNER JOIN bestellungen AS b USING(k_id)\r\n   INNER JOIN bestellpos AS bp USING(b_id)\r\n   INNER JOIN artikel AS a USING(a_id)\r\n   INNER JOIN segmente AS s USING(s_id)\r\nGROUP BY Kundenname, a.s_id\r\nORDER BY Kundenname, Segmentbezeichner'),
(8828, 475, 33, 15, 'select a_bezeichner, pl_einzelpreis as PohneMwst,s_mwst,(pl_einzelpreis*(1+s_mwst)) as PmMwst from (segmente inner join artikel on segmente.s_id=artikel.s_id) inner join preisliste2006 on artikel.a_id=preisliste2006.a_id order by s_mwst, PohneMwst');
