(8838, 455, 14, 16, 'SELECT k_name, -- Kundenname sum(pl_einzelpreis*bp_anzahl) as summe -- Summe des Positionsbetrages \r\nFROM (((kunden INNER JOIN bestellungen ON kunden.k_id=bestellungen.k_id) INNER JOIN bestellpos ON bestellpos.b_id=bestellungen.b_id) INNER JOIN preisliste2006 ON preisliste2006.a_id=bestellpos.a_id) \r\nGROUP BY kunden.k_id'),
