(9955, 526, 6, 14, 'SELECT s.s_bezeichner, k.k_name, b.b_datum\r\nFROM kunden AS k INNER JOIN bestellungen AS b USING(k_id)\r\n     INNER JOIN bestellpos USING(b_id)\r\n     INNER JOIN artikel AS a USING(a_id)\r\n     INNER JOIN segmente AS s USING (s_id)\r\nORDER BY k.k_name ASC, b.b_datum DESC;'),
(9956, 526, 22, 14, 'SELECT a.a_bezeichner, s.s_bezeichner, pl.pl_einzelpreis\r\nFROM Artikel AS a INNER JOIN Segmente AS s USING(s_id)\r\n     INNER JOIN preisliste2006 AS pl USING(a_id)\r\nWHERE pl.pl_einzelpreis >10 AND s.s_bezeichner ="Bücher"\r\nORDER BY pl.pl_einzelpreis DESC;'),
(9957, 526, 23, 14, 'SELECT DISTINCT kunden.k_name, bestellungen.b_datum, bestellpos.bp_anzahl, artikel.a_bezeichner\r\nFROM kunden LEFT JOIN bestellungen USING(k_id)\r\n     LEFT JOIN bestellpos ON bestellungen.k_id = bestellpos.a_id\r\n     LEFT JOIN artikel USING(a_id)\r\nORDER BY bestellungen.b_datum ASC;'),
